public void refreshItemTaxGroupDataSource()
{
    QueryBuildDataSource qbdsItemTaxGroup = procCategoryItemTaxGroup_ds.query().dataSourceTable(tablenum(ProcCategoryItemTaxGroup));

    qbdsItemTaxGroup.clearRanges();
