    }

    procCategoryItemTaxGroup_ds.executeQuery();
}

/// <summary>
/// Rebuilds the queries for the EcoResCategory_ForProductAttributes and
/// EcoResCategory_ForProduct datasources, then refreshes the grid listing all
/// product attributes of the selected category.
/// </summary>
public void refreshProductAttributeListGrid()
{
    QueryBuildDataSource qbdsCategoryAttributes = ecoResCategoryAttribute_Product_ds.query().dataSourceTable(tablenum(EcoResCategoryAttribute));

    // set the range for the category attribute modifier to product
    qbdsCategoryAttributes.clearRanges();
    qbdsCategoryAttributes.addRange(fieldnum(EcoResCategoryAttribute, Modifier)).value(queryValue(EcoResCategoryAttributeModifier::Product));
    qbdsCategoryAttributes.addRange(fieldnum(EcoResCategoryAttribute, Category)).value(queryValue(ecoResCategory.RecId));

    // finally, refresh UI
    ecoResCategoryAttribute_Product_ds.executeQuery();
}

