public void removeQuestionnaires()
{
    Common common;
    FormDataSource ds= procCategoryQuestionnaire_ds;
    ProcCategoryQuestionnaire tmpProcCat;
    int totalSelected = 0;
    int originalDeleted = 0;

    if (Box::yesNo("@SYS303265", DialogButton::No) == DialogButton::Yes)
    {
        common  = getFirstSelection(ds);

        ttsbegin;
        while (common)
        {
            ds.positionToRecord(common);
            totalSelected++;

            select RecId from tmpProcCat
                    where tmpProcCat.ProcurementCategory == ecoResCategory.RecId
                        && tmpProcCat.RecId == common.RecId;

            if ( tmpProcCat.RecId != 0)
            {
                originalDeleted++;
                procCategoryQuestionnaire.doDelete();
            }
            common = ds.getNext();
        }
        ttscommit;

        element.refreshQuestionnairesGrid();
        if (totalSelected > originalDeleted)
        {
            info(strfmt("@SYS302889", originalDeleted, (totalSelected - originalDeleted)));
        }
    }
}

public void removeVendors()
{
    VendProcurementCategoryStatus   selectedRecord;
    VendProcurementCategoryStatus   tmpVendCategoryStatus;
    VendCategory                    vendorCategory;
    VendCategoryRecId               vendorCategoryRecId;

    int             totalSelected   = 0;
    int             originalDeleted = 0;

    if (Box::yesNo("@SYS303265", DialogButton::No) == DialogButton::Yes)
    {
        selectedRecord  = getFirstSelection(vendProcurementCategoryStatus_ds);
        ttsbegin;
        while (selectedRecord)
        {
            totalSelected++;

            ttsbegin;
            select firstonly forupdate tmpVendCategoryStatus
                    where tmpVendCategoryStatus.RecId == selectedRecord.RecId
                    join vendorCategory
                        where vendorCategory.RecId == tmpVendCategoryStatus.VendorCategory
                            && vendorCategory.Category == ecoResCategory.RecId;

            if ( tmpVendCategoryStatus.RecId && tmpVendCategoryStatus.validateDelete() )
            {
                tmpVendCategoryStatus.delete();
                vendorCategoryRecId = tmpVendCategoryStatus.VendorCategory;
                originalDeleted++;
            }
            ttscommit;

            if (vendorCategoryRecId)
            {
                this.deleteVendorCategoryWithNoVendProcurementCategoryStatus(vendorCategoryRecId);
            }

            selectedRecord = vendProcurementCategoryStatus_ds.getNext();
        }
        ttscommit;
        element.refreshVendorsGrid();
        if (totalSelected > originalDeleted)
        {
            info(strfmt("@SYS304120", originalDeleted, (totalSelected - originalDeleted)));
        }
    }
}

