private void showHideValueFields(boolean _flag)
{
    ecoResValue_EcoResBooleanValue_BooleanValue.visible(_flag);
    ecoResValue_EcoResDateTimeValue_DateTimeValue.visible(_flag);
    ecoResValue_EcoResIntValue_IntValue.visible(_flag);
    ecoResValue_EcoResFloatValue_FloatValue.visible(_flag);
    descriptionTranslation.visible(_flag);
    ecoResValue_EcoResTextValue_TextValue.visible(_flag);
    ecoResValue_EcoResTextValue_EnumValue.visible(_flag);
    currencyValueGroup.visible(_flag);
    descriptionTranslation.enabled(_flag);
}

public void showValueField(AttributeDataType attributeDataType)
{
    EcoResTextValue enumerationValue;
    EcoResAttributeType enumerationDomain;
    EcoResEnumerationAttributeTypeValue enumerationAttributeTypeValue;

    element.showHideValueFields(false);
    switch (attributeDataType)
    {
        case AttributeDataType::DateTime :
            ecoResValue_EcoResDateTimeValue_DateTimeValue.visible(true);
            break;
        case AttributeDataType::Decimal :
            ecoResValue_EcoResFloatValue_FloatValue.visible(true);
            break;
        case AttributeDataType::Integer :
            ecoResValue_EcoResIntValue_IntValue.visible(true);
            break;
        case AttributeDataType::TrueFalse :
            ecoResValue_EcoResBooleanValue_BooleanValue.visible(true);
            break;
        case AttributeDataType::Text :
            if (ecoResAttributeType.IsEnumeration)
            {
                ecoResValue_EcoResTextValue_EnumValue.visible(true);
                // Populate enumeration combox box
                ecoResValue_EcoResTextValue_EnumValue.clear();
                while select TextValue from enumerationValue
                     join RecId from enumerationAttributeTypeValue
                       where enumerationAttributeTypeValue.Value == enumerationValue.RecId
                        join RecId from enumerationDomain
                            where enumerationDomain.RecId == enumerationAttributeTypeValue.AttributeType &&
                                  enumerationDomain.RecId == ecoResAttributeType.RecId
                {
                    ecoResValue_EcoResTextValue_EnumValue.add(enumerationValue.TextValue);
                }
            }
            else
            {
                ecoResValue_EcoResTextValue_TextValue.visible(true);
            }

            descriptionTranslation.visible(true);
            break;
        case AttributeDataType::Currency :
            currencyValueGroup.visible(true);
            break;
        default:
            attributeName.text("");
            attributeType.text("");
            break;
    }
}

