/// <summary>
/// Overriding behaviour of tasks
/// </summary>
/// <param name = "_taskId">Task identification</param>
/// <returns>Result of task execution</returns>
public int task(int _taskId)
{
    #task;
    int ret;

    ret = super(_taskId);

    if (_taskID == #taskRefresh)
    {
        element.rebuildCategoryHierarchyTree();
    }

    return ret;
}

