public void write()
{
    // If user deleted the text in the text box and if the record alreay exists then we disassociate
    if (strLen(strLRTrim(procCategoryItemTaxGroup.TaxItemGroup)) == 0)
    {
        if (procCategoryItemTaxGroup.RecId != 0)
