/// <summary>
/// checks for at least one category page is available for this category.
/// </summary>
/// <returns>
/// returns true if at least one category page is found.
/// </returns>
private boolean isCategoryPagesAvailable()
{
    ProcCategoryPage    localProcCategoryPage;

    localProcCategoryPage.clear();
    select firstOnly recId from localProcCategoryPage
        where localProcCategoryPage.ProcurementCategory == ecoResCategory.RecId;

    return localProcCategoryPage.RecId !=0;
}

public void modified()
{
    //proceed only if there are any category pages found for this category.
    if (this.isCategoryPagesAvailable())
    {
        categoryPageId = procCategoryCharterPagePolicy.ProcurementCategoryPage;
        procCategoryCharterPagePolicy_ds.write();
        element.refreshCategoryPagesTab();
    }
}

