display EcoResCategoryName getOriginationCategory(VendCategory _vendCategory)
{
    ProcCategoryModifier procCategory        = ProcCategoryModifier::find(ecoResCategory.RecId);
    EcoResCategory       ancestorCategory;
    EcoResCategory       tmpNearestStopInheritAncestor = ecoResCategory;
    VendCategory         ancestorVendCategory;

    if (procCategory.IsVendorsInherited)
    {
        tmpNearestStopInheritAncestor = ProcCategoryModifier::getNearestAncestorWithInheritUnchecked(ecoResCategory,
                                                                        fieldNum(ProcCategoryModifier, IsVendorsInherited) );
    }

    select firstonly NestedSetLeft, Name from ancestorCategory
        order by NestedSetLeft asc
        where ancestorCategory.CategoryHierarchy == tmpNearestStopInheritAncestor.CategoryHierarchy
            && ancestorCategory.NestedSetLeft   >= tmpNearestStopInheritAncestor.NestedSetLeft
            && ancestorCategory.NestedSetRight  <= tmpNearestStopInheritAncestor.NestedSetRight
            && ancestorCategory.NestedSetLeft   <= ecoResCategory.NestedSetLeft
            && ancestorCategory.NestedSetRight  >= ecoResCategory.NestedSetRight
        exists join ancestorVendCategory
            where ancestorVendCategory.VendorAccount    == _vendCategory.VendorAccount
                && ancestorVendCategory.VendorDataArea  == _vendCategory.VendorDataArea
                && ancestorVendCategory.Category        == ancestorCategory.RecId;

    return ancestorCategory.Name;
}

