void clicked()
{
    EcoResCategoryAttribute ecoResCategoryAttributeSelected;
    EcoResCategoryAttribute ecoResCategoryAttributeTemp;
    int                     nSelectedForDelete = 0;
    int                     nDeleted = 0;

    if (Box::yesNo("@SYS303265", DialogButton::No) == DialogButton::Yes)
    {
        ttsbegin;
        // Are there any marked records?
        for (ecoResCategoryAttributeSelected = getFirstSelection(ecoResCategoryAttribute_Product_ds);
        ecoResCategoryAttributeSelected;
        ecoResCategoryAttributeSelected = ecoResCategoryAttribute_Product_ds.getNext())
        {
            nSelectedForDelete++;
            ecoResCategoryAttributeTemp = EcoResCategoryAttribute::find(ecoResCategoryAttributeSelected.RecId, true);

            if ((ecoResCategoryAttributeTemp.Category == ecoResCategory.RecId) &&
                    ecoResCategoryAttributeTemp.Modifier == EcoResCategoryAttributeModifier::Product &&
                        ecoResCategoryAttributeTemp.validateDelete())
            {
                ecoResCategoryAttributeTemp.delete();
                nDeleted++;
            }
        }
        ttscommit;

        if (nDeleted > 0)
        {
            ecoResCategoryAttribute_Product_ds.executeQuery();
        }

        // some are inherited hence cannot be deleted - let use know
        if (nSelectedForDelete > nDeleted)
        {
            info(strfmt("@SYS135673", nDeleted, (nSelectedForDelete - nDeleted)));
        }
    }
    ecoResAttributeCategoryGrid.setFocus();
}

public boolean modified()
{
    boolean ret;

    ttsBegin;
    ret = super();

    // root node doesn't need refresh
    // cannot refresh for root node - the code called below may throw error, go into infinite loop, or give wrong results
    // because the root node's InheritAttributes may be set to TRUE now which violates our assumption
    // (this is assumption is enforced by EcoResCategory.validateWrite but at this point the change to InheritAttributes
    // isn't persisted yet - hence validateWrite hasn't executed
    // )
    if ( ecoResCategory.ParentCategory)
    {
        // persist the change before UI refresh
        // if we didn't persist, then UI refresh will have no effect since we still query the db for theInheritAttribute value.
        procCategoryModifier.write();
        element.refreshProductAttributeListGrid();
        element.refreshInheritedProductAttributeListGrid();
    }
    //call method to update/populate Category Attribute lookup table
    EcoResCategoryAttributeLookup::synchronizeWithCategoryAttribute(ecoResCategory);
    ttsCommit;

    return ret;
}

