void clicked()
{
    EcoResAttributeValue            selectedEcoResAttributeValue;
    EcoResCategoryInstanceValue     tmpEcoResCategoryInstanceValue;
    EcoResAttributeDefaultValue     tmpEcoResAttributeDefaultValue;

    ttsbegin;
    select RecId from tmpEcoResCategoryInstanceValue
            where tmpEcoResCategoryInstanceValue.Category           == ecoRescategory.RecId
                && tmpEcoResCategoryInstanceValue.BuyingLegalEntity == selectedLegalEntity.RecId;

    select firstonly forupdate selectedEcoResAttributeValue
            where selectedEcoResAttributeValue.Attribute == ecoResAttribute.RecId
                && selectedEcoResAttributeValue.InstanceValue == tmpEcoResCategoryInstanceValue.RecId;

    tmpEcoResAttributeDefaultValue = EcoResAttributeDefaultValue::findByAttribute(ecoResAttribute.RecId);

    if (tmpEcoResAttributeDefaultValue)
    {
        // Reset to the default value
        selectedEcoResAttributeValue.Value = EcoResValue::find(tmpEcoResAttributeDefaultValue.Value).pcDuplicate();
        selectedEcoResAttributeValue.update();
    }
    else
    {
        // Clear the value.
        selectedEcoResAttributeValue.delete();
    }

    ttscommit;

    ecoResAttributeValue_ds.executeQuery();
    if (this.checkAttributeDataTypeText())
    {
        if (selectedEcoResAttributeValue)
        {
            ecoResValue_EcoResTextValue_EnumValue.selectText(ecoResValue_EcoResTextValue.TextValue);
        }
        else
        {
            ecoResValue_EcoResTextValue_EnumValue.selection(0);
        }
        element.enumerationChanged();
    }
}

/// <summary>
///     Checks if <c>AttributeDataType</c> value is Text type and <c>IsEnumeration</c> value exists and returns a boolean value.
/// </summary>
/// <returns>
///     true if <c>AttributeDataType</c> value is Text type and <c>IsEnumeration</c> value exists; Otherwise, false.
/// </returns>
protected boolean checkAttributeDataTypeText()
{
    return (ecoResAttributeType.DataType == AttributeDataType::Text  && ecoResAttributeType.IsEnumeration);
}

