public void init()
{
    DirPartyTable           vendorParty;
    Common                  commonForEmptyTitleDS;

    super();

    selectedLegalEntity = CompanyInfo::findDataArea(curext());
    buyingOrganization.value(selectedLegalEntity.RecId);

    if (element.args() && element.args().dataset() == tablenum(VendCategory))
    {
        selectedVendCategory = element.args().record();
        caller = element.args().caller();
        selectedLegalEntity = caller.getSelectedLegalEntity();
        buyingOrganization.value(selectedLegalEntity.RecId);
        if (selectedVendCategory)
        {
            select  firstonly crosscompany RecId from vendorParty
                order by vendorParty.Name
                join selectedVendTable
                where selectedVendTable.DataAreaId  == selectedVendCategory.VendorDataArea
                    && selectedVendTable.Party      == vendorParty.RecId
                    && selectedVendTable.AccountNum == selectedVendCategory.VendorAccount;

            vendAccountNum.text(selectedVendTable.AccountNum);
            selectedLegalEntity = selectedVendTable.companyInfo();

            categoryReferenceGroup.value(selectedVendCategory.Category);
        }
    }

    procurementCategoryHierarchy = EcoResCategoryHierarchy::find(EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Procurement).CategoryHierarchy);

    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).clearLinks();
    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).clearDynalinks();
    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).addLink(fieldnum(VendCategory,Category),fieldnum(VendReviewCategoryCriterionLookup,Category));
    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).joinMode(JoinMode::InnerJoin);

    vendReviewCategoryCriterionLookup_Others_ds.query().dataSourceTable(tablenum(VendReviewCategoryCriterionLookup)).clearLinks();
    vendReviewCategoryCriterionLookup_Others_ds.query().dataSourceTable(tablenum(VendReviewCategoryCriterionLookup)).clearDynalinks();
    vendReviewCategoryCriterionLookup_Others_ds.query().dataSourceTable(tablenum(VendReviewCategoryCriterionLookup)).addLink(fieldnum(EcoResCategory,RecId), fieldnum(VendReviewCategoryCriterionLookup,Category));
    vendReviewCategoryCriterionLookup_Others_ds.query().dataSourceTable(tablenum(VendReviewCategoryCriterionLookup)).joinMode(JoinMode::InnerJoin);

    ecoResCategory_Others_ds.query().dataSourceTable(tablenum(EcoResCategory)).clearDynalinks();

    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).clearLinks();
    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).clearDynalinks();
    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).addLink(fieldnum(VendCategory,Category),fieldnum(VendReviewCategoryCriterionLookup,Category));
    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).joinMode(JoinMode::InnerJoin);

    vendReviewCriterionRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionRating)).sortClear();

    vendReviewCriterionGroupTranslation_ds.query().dataSourceTable(tablenum(VendReviewCriterionGroupTranslation)).addRange(fieldnum(VendReviewCriterionGroupTranslation, LanguageId)).value(LanguageTable::defaultLanguage());
    vendReviewCriterionTranslation_ds.query().dataSourceTable(tablenum(VendReviewCriterionTranslation)).addRange(fieldnum(VendReviewCriterionTranslation, LanguageId)).value(LanguageTable::defaultLanguage());

    // To clear dynamic Title dataSource, need to pass either null or empty table buffer.
    element.args().record(commonForEmptyTitleDS);
}

/// <summary>
/// Refreshes the criterion group grid for the selected category.
/// </summary>
public void refreshCategoryCriterionGroupGrid()
{
    vendReviewCriterionGroupRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionGroupRating)).clearRanges();
    vendReviewCriterionGroupRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionGroupRating)).addRange(fieldnum(VendReviewCriterionGroupRating, VendorAccountNumber)).value(queryValue(selectedVendTable.AccountNum));
    vendReviewCriterionGroupRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionGroupRating)).addRange(fieldnum(VendReviewCriterionGroupRating, VendorDataAreaId)).value(queryValue(selectedVendTable.DataAreaId));

    vendReviewCriterionRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionRating)).clearRanges();
    vendReviewCriterionRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionRating)).addRange(fieldnum(VendReviewCriterionRating, VendorAccountNumber)).value(queryValue(selectedVendTable.AccountNum));
    vendReviewCriterionRating_ds.query().dataSourceTable(tablenum(VendReviewCriterionRating)).addRange(fieldnum(VendReviewCriterionRating, VendorDataAreaId)).value(queryValue(selectedVendTable.DataAreaId));

    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).clearRanges();
    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).addRange(fieldnum(VendCategory, VendorAccount)).value(queryValue(selectedVendTable.AccountNum));
    vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).addRange(fieldnum(VendCategory, VendorDataArea)).value(queryValue(selectedVendTable.DataAreaId));

    if (ecoResSelectedCategory.RecId)
    {
        vendCategory_ds.query().dataSourceTable(tablenum(VendCategory)).addRange(fieldnum(VendCategory, Category)).value(queryValue(ecoResSelectedCategory.RecId));
    }

    vendReviewCriterionGroup_ds.executeQuery();
}

/// <summary>
/// This is used to fill the other applicable categories by looping through the VendReviewCategoryCriterionGroup records.
/// </summary>
public void refreshOtherCategoriesListView()
{
    vendReviewCategoryCriterionLookup_Others_ds.query().dataSourceTable(tablenum(VendReviewCategoryCriterionLookup)).clearRanges();
    vendReviewCategoryCriterionLookup_Others_ds.query().dataSourceTable(tablenum(VendReviewCategoryCriterionLookup)).addRange(fieldnum(VendReviewCategoryCriterionLookup, CriterionGroup)).value(queryValue(vendReviewCriterionGroup.RecId));

    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).clearRanges();
    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).addRange(fieldnum(VendCategory, VendorAccount)).value(queryValue(selectedVendTable.AccountNum));
    vendCategory_Others_ds.query().dataSourceTable(tablenum(VendCategory)).addRange(fieldnum(VendCategory, VendorDataArea)).value(queryValue(selectedVendTable.DataAreaId));

    ecoResCategory_Others_ds.executeQuery();
}

