public boolean validateWrite()
{
    boolean ret;

    if (vendReviewCriterionRating.RecId == 0)
    {
        vendReviewCriterionRating.VendorAccountNumber   = selectedVendTable.AccountNum;
        vendReviewCriterionRating.VendorDataAreaId      = selectedVendTable.DataAreaId;
        vendReviewCriterionRating.Criterion             = vendReviewCriterion.RecId;
        vendReviewCriterionRating.ValidFrom             = DateTimeUtil::utcNow();
        vendReviewCriterionRating.ValidTo               = DateTimeUtil::maxValue();
    }
    // where new or existing record, we need to ensure Evaluator is set to the last one who made change
    vendReviewCriterionRating.Evaluator                 = HcmWorker::userId2Worker(curuserid());

    // Evaluator is Mandatory
    // - adding custom error here avoids the generic error (from super())
    //      "Field 'Reference' must be filled in."
    if ( ! vendReviewCriterionRating.Evaluator)
    {
        warning("@SYS345398");
        ret = false;
    }
    else
    {
        ret = super();
    }

    return ret;
}

