/// Lookup for vendors in the context of buying legal entity.
/// </summary>
public void lookup()
{
    RefRecId companyInfoId = buyingOrganization.value();

    if (companyInfoId == 0)
    {
        companyInfoId = CompanyInfo::findDataArea(curext()).RecId;
    }

    changecompany(CompanyInfo::findRecId(companyInfoId).DataArea)
    {
        super();
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    changeCompany(selectedLegalEntity.DataArea)
    {
        selectedVendTable = VendTable::findByDataAreaId(this.valueStr(), selectedLegalEntity.DataArea);
    }

    categoryReferenceGroup.value(0);
    selectedVendCategory.RecId = 0;

    element.refreshCategoryCriterionGroupGrid();

    return ret;
}

