public Common lookupReference()
{
    SysReferenceTableLookup sysTableLookup;
    Query                   query = new Query();
    QueryBuildDataSource    qbdsCategory, qbdsVendorCategory;

    qbdsCategory          = query.addDataSource(tablenum(EcoResCategory));
    qbdsVendorCategory    = qbdsCategory.addDataSource(tablenum(VendCategory));
    qbdsVendorCategory.relations(false);
    qbdsVendorCategory.joinMode(JoinMode::ExistsJoin);
    qbdsVendorCategory.addLink(fieldnum(EcoResCategory, RecId), fieldnum(VendCategory, Category));
    qbdsVendorCategory.addRange(fieldnum(VendCategory,VendorAccount)).value(queryValue(selectedVendTable.AccountNum));
    qbdsVendorCategory.addRange(fieldnum(VendCategory,VendorDataArea)).value(queryValue(selectedVendTable.DataAreaId));
    qbdsCategory.addRange(fieldnum(EcoResCategory,CategoryHierarchy)).value(queryValue(procurementCategoryHierarchy.RecId));

    sysTableLookup              = SysReferenceTableLookup::newParameters(tablenum(EcoResCategory), this);
    sysTableLookup.addLookupfield(fieldnum(EcoResCategory, Name));
    sysTableLookup.addLookupMethod(tablemethodstr(EcoResCategory, description));
    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

