public Common resolveReference()
{
    Common                  selectedRecord;
    EcoResCategory          ecoResCategory;
    VendCategory            vendCategoryLocal;
    EcoResCategoryName      categoryName;

    categoryName = this.filterValue(AbsoluteFieldBinding::construct(fieldstr(EcoResCategory, Name), tablestr(EcoResCategory))).value();

    select count(RecId) from ecoResCategory
            exists join vendCategoryLocal
                where vendCategoryLocal.Category        == ecoResCategory.RecId
                    && vendCategoryLocal.VendorAccount  == selectedVendTable.AccountNum
                    && vendCategoryLocal.VendorDataArea == selectedVendTable.DataAreaId
                    && ecoResCategory.Name              == categoryName
                    && ecoResCategory.CategoryHierarchy == procurementCategoryHierarchy.RecId;

    if (ecoResCategory.RecId == 0)
    {
        // Assigning null to selectedRecord does not cause the kernel to receive a true NULL value, but rather
        // just an empty record. Return NULL directly so the kernel knows that the intent is to not clear the
        // selected value.
        return null;
    }
    else if (ecoResCategory.RecId > 1)
    {
        selectedRecord = this.lookupReference();
    }
    else
    {
        select firstonly * from ecoResCategory
            exists join vendCategoryLocal
                where vendCategoryLocal.Category        == ecoResCategory.RecId
                    && vendCategoryLocal.VendorAccount  == selectedVendTable.AccountNum
                    && vendCategoryLocal.VendorDataArea == selectedVendTable.DataAreaId
                    && ecoResCategory.Name              == categoryName
                    && ecoResCategory.CategoryHierarchy == procurementCategoryHierarchy.RecId;

        selectedRecord = ecoResCategory;
    }

    return selectedRecord.RecId? selectedRecord : null;
}
