[Form]
public class ProdAsBuiltBOMProductComponent extends FormRun
{
    private ProdAsBuiltBOMAssociationController        associationController;
    private ProdAsBuiltBOMProductComponentUIController uiController;
    private ProdAsBuiltBOMProductComponent             scanProductState;
    private InventDimCtrl_Frm_Mov_ProductComponentLot  productInventDimFormSetup;
    private InventDimCtrl_Frm_Mov_ProductComponentLot  componentInventDimFormSetup;
    private ProdAsBuiltBOMProductComponent             productComponentParmSelection;

}

public void init()
{
    if (!element.args().dataset())
    {
        throw error(strFmt("@SYS22678",element.name()));
    }

    associationController = ProdAsBuiltBOMAssociationController::construct();
    element.args().parmEnum(ProdAsBuiltBOMInputOrigin::Standard);
    uiController = ProdAsBuiltBOMProductComponentUIController::construct(associationController, element.args());

    super();

    boolean autoScanResult = uiController.autoScanProductLot();
    if (autoScanResult)
    {
        element.switchToComponents();
    }
    else
    {
        element.switchToProducts();
    }
}

internal boolean performRegistrationAction()
{
    boolean result = uiController.performRegistrationAction(Input.valueStr(), scanProductState, prodAsBuiltBOMFormulaProducts, prodBOM);

    if (result)
    {
        if (scanProductState == ProdAsBuiltBOMProductComponent::Product)
        {
            ProdAsBuiltBOMFormulaProducts_ds.reread();
            ProdAsBuiltBOMFormulaProducts_ds.refresh();
            element.switchToComponents();
        }
        if (scanProductState == ProdAsBuiltBOMProductComponent::Component)
        {
            ProdBOM_ds.reread();
            ProdBOM_ds.refresh();
        }
        element.clearInput();
    }
    Input.setFocus();

    return result;
}

internal void switchToProducts()
{
    scanProductState = ProdAsBuiltBOMProductComponent::Product;
    element.updateScanButtons();
}

internal void switchToComponents()
{
    scanProductState = ProdAsBuiltBOMProductComponent::Component;
    element.updateScanButtons();
}

internal void updateScanButtons()
{
    if (scanProductState == ProdAsBuiltBOMProductComponent::Product)
    {
        scanProduct.toggleValue(1);
        scanComponent.toggleValue(0);
    }
    else
    {
        scanProduct.toggleValue(0);
        scanComponent.toggleValue(1);
    }

    Input.setFocus();
}

public Object inventDimSetupObject()
{       
    if (productComponentParmSelection == ProdAsBuiltBOMProductComponent::Product)
    {
        return productInventDimFormSetup;
    }

    return componentInventDimFormSetup;
}

internal Common product()
{
    ProdAsBuiltBOMFormulaProducts product = ProdAsBuiltBOMFormulaProducts_ds.cursor();

    if (product.isCoByProduct())
    {
        return PmfProdCoBy::findTransId(product.InventTransId);
    }

    return ProdTable::findTransId(product.InventTransId);
}

internal void updateProductDesign(InventDimFormDesignUpdate mode)
{
    InventMovement inventMovement = InventMovement::construct(this.product());

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!productInventDimFormSetup)
            {
                Set validDataSources = new Set(Types::String);
                validDataSources.add(ProdReportFinishProductsInventDim_ds.name());
                productInventDimFormSetup = InventDimCtrl_Frm_Mov_ProductComponentLot::newFromFormMovement(element, inventMovement, validDataSources);
            }
        // Fall through
        case InventDimFormDesignUpdate::Active:
            productInventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            productInventDimFormSetup.parmDimParmEnabled(productInventDimFormSetup.parmDimParmEnabled());
            productInventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            productInventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            productInventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

internal void updateComponentDesign(InventDimFormDesignUpdate mode)
{
    var inventMovement = InventMovement::construct(prodBOM);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!componentInventDimFormSetup)
            {
                Set validDataSources = new Set(Types::String);
                validDataSources.add(ProdBOMInventDim_ds.name());
                componentInventDimFormSetup = InventDimCtrl_Frm_Mov_ProductComponentLot::newFromFormMovement(element, inventMovement, validDataSources);
            }
        // Fall through
        case InventDimFormDesignUpdate::Active:
            componentInventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            componentInventDimFormSetup.parmDimParmEnabled(componentInventDimFormSetup.parmDimParmEnabled());
            componentInventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            componentInventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            componentInventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public Args createInventoryArgs()
{
    Args args = new Args();
    args.caller(this);
    args.record(element.product());

    return args;
}

public void clearInput()
{
    Input.text('');
}

public void init()
{
    super();
    QueryBuildDataSource qbdsProdAsBuiltBOMFormulaProducts = this.query().dataSourceTable(tablenum(ProdAsBuiltBOMFormulaProducts));
    uiController.initProdAsBuiltBOMFormulaProductsDatasource(qbdsProdAsBuiltBOMFormulaProducts);

    element.updateProductDesign(InventDimFormDesignUpdate::Init);
}

public display ProdAsBuiltBOMProductComponentStatus productLotStatus(ProdAsBuiltBOMFormulaProducts _productLot)
{
    return uiController.productLotStatus(_productLot);
}

public int active()
{        
    int ret = super();
    element.updateProductDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void init()
{
    super();
    QueryBuildDataSource qbdsProdBOM = this.query().dataSourceTable(tablenum(ProdBOM));
    uiController.initProdBOMDatasource(qbdsProdBOM);

    element.updateComponentDesign(InventDimFormDesignUpdate::Init);
}

