}

public int active()
{
    int ret = super();        
    element.updateComponentDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public boolean leave()
{
    boolean ret = super();

    if (ret && element.performRegistrationAction())
    {
        ret = false; // Do not leave the input
    }

    return ret;
}

