void enableFields()
{
    ProdTable   prodTableRef;
    FormDataObject  reservationCtrl;

    if (prodBOM.isProjMethodConsumed())
    {
        if (prodBOM.ProdLineType == BOMType::PeggedSupply)
        {
            prodTableRef = prodBOM.prodTableRef();
            prodBOM_ds.object(fieldNum(ProdBOM,ProdLineType)).allowEdit(prodTableRef ? false : true);
            prodBOM_ds.object(fieldNum(ProdBOM,ProjSetSubProdToConsumed)).allowEdit(prodTableRef ? false : true);
        }
        else
        {
            prodBOM_ds.object(fieldNum(ProdBOM,ProdLineType)).allowEdit(true);
            prodBOM_ds.object(fieldNum(ProdBOM,ProjSetSubProdToConsumed)).allowEdit(false);
        }
    }

    reservationCtrl = prodBOM_ds.object(fieldNum(ProdBOM, Reservation));
    if (reservationCtrl.allowEdit() || disabledByProdLineType)
    {
        reservationCtrl.allowEdit(prodBOM.ProdLineType != BOMType::PeggedSupply);
        disabledByProdLineType = !reservationCtrl.allowEdit();
    }
}

/// <summary>
/// Customizes the options used to populate the Office Menu. Remove the <c>ProdProductionOrderBillOfMaterialsLineEntity</c> 
/// added by the framework and add a new custom menu item for which we can add a filter.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    // Clear the menu options which are driven by the root data source of an entity,
    // we need to be able to set a filter for the related production order in getDataEntityContext.
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());
    while (dataEntityIterator.more())
    {
        dataEntityIterator.delete();
    }

    str dataEntityName = '';
    str dataEntityLabel;

    switch (this.ProdType())
    {
        case ProdType::Process:
            dataEntityName = tableStr(ProdBatchOrderFormulaLineEntity);
            dataEntityLabel = "@SCM:ProdBatchOrderFormulaLineEntity";
            break;

        case ProdType::Standard:
        case ProdType::Vendor:
            dataEntityName = tableStr(ProdProductionOrderBillOfMaterialsLineEntity);
            dataEntityLabel = "@SCM:ProdProductionOrderBillOfMaterialsLineEntityName";
            break;
    }

    if (dataEntityName != '')
    {
        OfficeGeneratedExportMenuItem exportMenuItem = OfficeGeneratedExportMenuItem::construct(
            dataEntityName,
            CustomExportLinesToExcelOptionId);

        exportMenuItem.displayName(strfmt("@SYS76681", dataEntityLabel, '', curExt()));

        _menuOptions.customMenuItems().addEnd(exportMenuItem);
    }
}

/// <summary>
/// Gets the <see cref="ExportToExcelDataEntityContext"/> that specifies what should be exported for the specified menu item. 
/// The <c>ProdProductionOrderBillOfMaterialsLineEntity</c> export is filtered based on related production order.
/// </summary>
/// <param name="_menuItem">The menu item.</param>
/// <returns>
/// Returns the <see cref="ExportToExcelDataEntityContext"/> that specifies what should be exported for the specified menu item;
/// otherwise, null.
/// </returns>
public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    if (_menuItem.id() == CustomExportLinesToExcelOptionId)
    {
        str dataEntityName = '';
        str fieldGroupName;
        str dataAreaIdFieldName;
        str orderNumber;

        switch (this.ProdType())
        {
            case ProdType::Process:
                dataEntityName = tableStr(ProdBatchOrderFormulaLineEntity);
                fieldGroupName = tableFieldGroupStr(ProdBatchOrderFormulaLineEntity, AutoReport);
                dataAreaIdFieldName = fieldStr(ProdBatchOrderFormulaLineEntity, DataAreaId);
                orderNumber = fieldStr(ProdBatchOrderFormulaLineEntity, BatchOrderNumber);
                break;

            case ProdType::Standard:
            case ProdType::Vendor:
                dataEntityName = tableStr(ProdProductionOrderBillOfMaterialsLineEntity);
                fieldGroupName = tableFieldGroupStr(ProdProductionOrderBillOfMaterialsLineEntity, AutoReport);
                dataAreaIdFieldName = fieldStr(ProdProductionOrderBillOfMaterialsLineEntity, DataAreaId);
                orderNumber = fieldStr(ProdProductionOrderBillOfMaterialsLineEntity, ProductionOrderNumber);
                break;
        }

        if (dataEntityName != '')
        {
            context = ExportToExcelDataEntityContext::construct(dataEntityName, fieldGroupName);

            ListEnumerator enumerator = context.entities().getEnumerator();
            while (enumerator.moveNext())
            {
                ExportToExcelDataEntityInfo entity = enumerator.current();

                if (entity.entityName() == dataEntityName)
                {
                    ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(dataEntityName);

                    var filter = filterBuilder.and(
                        filterBuilder.areEqual(dataAreaIdFieldName, curExt()),
                        filterBuilder.areEqual(orderNumber, this.prodId()));

                    entity.filter(filter);
                    break;
                }
            }
        }
    }

    return context;
}

