void init()
{
    pmfFormCtrl_ProdBOM = PmfFormCtrl_ProdBOM::newFromForm(element);
    pmfFormCtrl_ProdBOM.initPre();

    cwFormCtrl_ProdBOMStep = PdsCWFormCtrl_ProdBOM_Step::newFromForm(element);

    Set formulaEnumSet = PmfFormCtrl_BOMHelper::buildFormulaEnumSet(pmfFormCtrl_ProdBOM.isFormulaEnabled());
    formulaComboBox = SysFormEnumComboBox::newParameters(element, element.controlId(formControlStr(ProdBOM, ConsumptionCalculation_Formula)),  enumNum(BOMFormula), formulaEnumSet);

    super();

    if (! element.args().caller()  &&
          element.args().dataset() != tableNum(ProdTable) &&
          element.args().dataset() != tableNum(ReqTrans))
    {
        throw error(strFmt("@SYS22494",element.name(),tableStr(ProdTable),tableStr(ReqTrans)));
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    pmfFormCtrl_ProdBOM.setDatasources(prodBOM);
    pmfFormCtrl_ProdBOM.initPost();
    cwFormCtrl_ProdBOMStep.setDatasources(pmfProdBOMStep, prodBOM);

    if (!WHSProdReducePickedQuantityFlight::instance().isEnabled())
    {
        WHSProdBOMPool_ds.linkType(FormLinkType::Passive);
    }
}

