void setmandatory()
{
    if (prodBOM)
    {
        project.visible(prodBOM.isProjMethodConsumed());

        project.allowEdit(prodBOM.InventRefType == InventRefType::Production ? prodTable.ProdStatus != ProdStatus::Completed : prodStatus.bomAllowEdit());

        prodBOM_ds.object(fieldNum(ProdBOM,ProjTransDate)).mandatory(prodBOM.isProjMethodConsumed());
        prodBOM_ds.object(fieldNum(ProdBOM,ProjCategoryId)).mandatory(prodBOM.isProjMethodConsumed());
        prodBOM_ds.object(fieldNum(ProdBOM,ProjLinePropertyId)).mandatory(prodBOM.isProjMethodConsumed());
        prodBOM_ds.object(fieldNum(ProdBOM,ProjSalesCurrencyId)).mandatory(prodBOM.isProjMethodConsumed());
        prodBOM_ds.object(fieldNum(ProdBOM,UnitId)).mandatory(prodBOM.isProjMethodConsumed());

        projectUnder.visible(isUnderconsumed);
        if (isUnderconsumed)
        {
            projectUnder.allowEdit(prodTable.ProdStatus != ProdStatus::Completed);

            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,TransDate)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjCategoryId)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjLinePropertyId)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjSalesCurrencyId)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjSalesUnitId)).mandatory(true);
        }
    }
    else
    {
        project.visible(false);
        projectUnder.visible(false);
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm   inventDimParmEnabled;

    inventMovement = InventMovement::construct(prodBOM);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            inventMovement = InventMovement::construct(prodBOM);

            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_Mov_ProdBom::newFromFormMovement(element, inventMovement);

        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);

            inventDimParmEnabled = inventDimFormSetup.parmDimParmEnabled();
            inventDimParmEnabled.InventLocationIdFlag = (prodBOM.WrkCtrConsumption ? NoYes::No : NoYes::Yes);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);

            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange   :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

