public boolean validateWrite()
{
    boolean ret;

    prodBOM.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    if (prodBOM.prodTableRef().isProjMethodConsumed())
    {
        if (ret && prodBOM.prodTableRef().prodTableProj(ProjItemTypeProdTable::Underconsumed))
            ret = element.projJournalFormTransUnder(prodTableProjUnder, prodBOM.prodTableRef()).validateWritePost();
    }

    InventTable         inventTable  = prodBOM.inventTable();
    UnitOfMeasureSymbol inventUnitId = inventTable.inventUnitId();

    if (!prodBOM.RecId
        && EcoResProductUnitConverter::hasRequiredProductDimensionsForUnitConversion(prodBOM.ItemId, inventDim)
        && !EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(prodBOM.ItemId,
                                                                                         prodBOM.InventDimId,
                                                                                         prodBOM.UnitId,
                                                                                         inventUnitId))
    {
        return checkFailed(strFmt("@SCM:ProductVariantConversionMissing", prodBOM.UnitId, inventUnitId, EcoResProductMaster::find(inventTable.Product).productNumber()));
    }

    return ret;
}

void write()
{
    boolean setupChanged;

    InventMovement::bufferSetRemainQty(prodBOM);

    if (skipWrite)
    {
        return;
    }
    pmfFormCtrl_ProdBOM.dataSourceWritePre();

    ttsbegin;

    prodBOM.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    if (prodBOM.prodTableRef().isProjMethodConsumed())
    {
        if (prodBOM.prodTableRef().prodTableProj(ProjItemTypeProdTable::Underconsumed))
        {
            prodTableProjUnder.ProdId = prodBOM.prodTableRef().ProdId;
            prodTableProjUnder.Type = ProjItemTypeProdTable::Underconsumed;
            if (prodTableProjUnder.RecId)
                prodTableProjUnder.update();
            else
                prodTableProjUnder.insert();
        }
    }

    setupChanged = prodBOM.hasSetupChanged();

    super();

    if (prodBOM.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(prodBOM.InventDimId));
        inventDim_ds.setCurrent();
    }

    ttscommit;

    if (setupChanged && prodTable.status().isAfterOrEqualTo(ProdStatus::Created) && prodTable.status().isBefore(ProdStatus::ReportedFinished))
    {
        prodTableChangeQtySched::newProdTable(prodTable).runOperation();

        if (ProdBOMFormDSRefreshAfterCreateFlight::instance().isEnabled())
        {
            prodBOM_ds.research(true);
        }
        else
        {
            prodBOM_ds.reread();
        }
    }

}

void linkActive()
{
    ReqTrans    reqTrans;

    if (element.args().dataset() == tableNum(ProdTable))
    {
        prodTable  = element.args().record();
        QueryBuildRange qbr = SysQuery::findOrCreateRange(this.query().dataSourceTable(tablenum(ProdBOM)), fieldnum(ProdBOM, ProdId));
        qbr.value(prodTable.ProdId);
    }
    else if (element.args().dataset() == tableNum(ReqTrans))
    {
        reqTrans = element.args().record();
        if (reqTrans.RefType != ReqRefType::Production)
        {
            element.close();
            return;
        }

        prodTable = ProdTable::find(reqTrans.RefId);
    }
    else if (element.args().dataset() == tableNum(ProdBOM))
    {
        ProdBOM prodBOMLocal    = element.args().record();
        prodTable               = prodBOMLocal.prodTable();
        QueryBuildRange qbr     =  this.query().dataSourceTable(tablenum(ProdBOM)).findRange(fieldnum(ProdBOM, RecId));
        if (!qbr)
        {
            qbr = this.query().dataSourceTable(tablenum(ProdBOM)).addRange(fieldnum(ProdBOM, RecId));
        }
        qbr.value(queryValue(prodBOMLocal.RecId));
    }

    prodStatus = prodTable.statusClient();

    overview.allowEdit(prodStatus.bomAllowEdit());
    general.allowEdit(prodStatus.bomAllowEdit());
    setup.allowEdit(prodStatus.bomAllowEdit());
    updateNow.allowEdit(prodStatus.bomAllowEdit());
    dimensions.allowEdit(prodStatus.bomAllowEdit());

    pmfFormCtrl_ProdBOM.dataSourceLinkActivePre();

    super();

    ctrlBOMRouteCopy.enabled(prodStatus.runCopy(true));
}

void create(boolean append = false)
{
    element.prodBOMForceWrite();

    pmfFormCtrl_ProdBOM.dataSourceCreatePre();

    super(append);

    inventDim_ds.create();

    prodBOM.initFromProdTable(prodTable);

    if (prodBOM.isProjMethodConsumed())
        prodBOM.ProjTransDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    pmfFormCtrl_ProdBOM.dataSourceCreatePost(prodBOM);
}

int active()
{
    int ret;
    DictEnum    dictEnum;
    int         i;

    ret = super();

    pmfFormCtrl_ProdBOM.dataSourceActivePost();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        isUnderconsumed = (prodBOM ? prodBOM.ProjId && prodBOM.prodTableRef().isProjMethodConsumed() && prodBOM.prodTableRef().prodTableProj(ProjItemTypeProdTable::Underconsumed) : false);

        skipWrite = true;
        if (isUnderconsumed)
        {
            prodTableProjUnder_ds.executeQuery();
        }
        skipWrite = false;
    }

    element.updateDesign(InventDimFormDesignUpdate::Active);

    InventTable prodBomItem = prodBOM.inventTable();
    if (#PdsMRCEnabled)
    {
        pdsMRCOpenMSDSDocument.enabled(prodBomItem.pdsMRCRegulatedMaterial());
    }

    if (#pmfEnabled)
    {
        consumptionCalculation_Formula.enabled(true);
        consumptionCalculation_Formula.allowEdit(true);
        ProdBOM_BOMQtySerie.noOfDecimals(UnitOfMeasure::findBySymbol(InventTable::find(prodTable.ItemId).inventUnitId()).DecimalPrecision);
        formulaComboBox.select(enum2int(prodBOM.Formula));
    }

    element.enableStepConsumption(prodBOM);

    element.setmandatory();
    if (prodBOM.prodTable().isProjMethodConsumed())
    {
        costControlCommittedCost.enabled(true);
    }
    else
    {
        costControlCommittedCost.enabled(false);
    }
    element.enableFields();

    this.enableInventTransProductionBOMConsignment();

    return ret;
}

private void enableInventTransProductionBOMConsignment()
{
    boolean enabled = prodBOM.inventTable().inventDimParmActiveDimensions().InventOwnerIdFlag_RU;
    enabled = enabled && InventTransOriginProdBomOwnershipChangeJournalTransLink::findByProductionBOMLine(prodBOM.DataAreaId, prodBOM.InventTransId).RecId != 0;

    InventTransProductionBOMConsignment.enabled(enabled);
}

