    if (prodBOM.isProjMethodConsumed())
    {
        if (prodBOM.ProdLineType == BOMType::PeggedSupply)
        {
            prodBOM.ProjSetSubProdToConsumed = ProjParameters::find().ProjSetSubProdToConsumed;
        }
        else
        {
            prodBOM.ProjSetSubProdToConsumed = NoYes::No;
        }
    }

    element.enableFields();
}

