public void lookup(FormControl _formControl, str _filterStr)
{
    if (!EcoResProductMasterConfigurationMgr::configure( _formControl,
                                                        prodBOM.ItemId,
                                                        InventDim::findOrCreate(inventDim).InventDimId,
                                                        prodBOM.prodTable().bomDate,
                                                        prodBOM.ItemBomId))
    {
