                                         prodBOM.ItemId);
    }
}

/// <summary>
/// Validate if change to field is allowed as part of Batch Disposition functionality.
/// </summary>
/// <returns>True if change allowed; Otherwise false.</returns>
public boolean validate()
{
    boolean ret = super();

    if (ret
        && #PdsShelfEnabled
        && !InventBatch::pdsCheckExpiryDate(inventDim.InventBatchId,
                                            prodBOM.ItemId,
                                            prodTable.DlvDate))
    {
        ret = checkFailed("@PRO910");
    }

    if (ret)
    {
        InventBatch::pdsCheckDisposition(inventDim.InventBatchId,
                                         prodBOM.ItemId);
    }

    return ret;
}

