public void executeQuery()
{
    QueryBuildDataSource  qbds;
    Query                 query;

    if (!prodBOM.ProjId)
        return;

    query = this.query();
    qbds = query.dataSourceName(this.name());
    qbds.clearRanges();
    qbds.clearDynalinks();
    qbds.addRange(fieldNum(ProdTableProj, ProdId)).value(prodBOM.prodIdRef());
    qbds.addRange(fieldNum(ProdTableProj, Type)).value(queryValue(ProjItemTypeProdTable::Underconsumed));

    super();
}

