/// <summary>
/// Initializes and calls PdsMRCDocument with the inventTable record of the current item
/// </summary>
void clicked()
{
    Args    args;

    super();
    args = new Args();
    args.caller(element);
    args.record(InventTable::find(prodBOM.ItemId));

    FormRun formRun = new MenuFunction(menuitemDisplayStr(PdsMRCDocument), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();
}

