[Form]
public class ProdBOMChangeItemDialog extends FormRun
{
    ProdBOMMultiChangeItem controller;
    InventDimCtrl_Frm fromInventDimCtrl, toInventDimCtrl;
    QueryBuildRange parmQuery;   
    ProdQty toQty;
    boolean isProdBOMChangeItemUseDefaultOrderSettingsFeatureEnabled;

    [FormObservable]
    InventDimId fromInventDimId;

    [FormObservable]
    InventDimId toInventDimId;

    [FormObservable]
    ItemId fromItemId;

    [FormObservable]
    ItemId toItemId;

}

void closeOk()
{
    if (controller.checkCloseDialog())
    {
        super();
    }
}

public void init()
{
    super();

    controller = element.args().caller().runbase();    

    isProdBOMChangeItemUseDefaultOrderSettingsFeatureEnabled = ProdBOMChangeItemUseDefaultOrderSettingsFeature::instance().isEnabled();

    element.updateDesignFromItem(InventDimFormDesignUpdate::Init);
    element.updateDesignToItem(InventDimFormDesignUpdate::Init);

    ProdBOMParmChangeItem_ds.query()
        .dataSourceTable(tableNum(ProdBOMParmChangeItem))
        .addRange(fieldNum(ProdBOMParmChangeItem, ParmId))
        .value(queryValue(controller.parmId()));
}

void doReselect()
{
    this.tryLoadData();
}

public void updateDesignFromItem(InventDimFormDesignUpdate _mode)
{
    this.updateDesignForItem(_mode, fromItemId, FromInventDim_ds, fromInventDimCtrl);
}

public void updateDesignToItem(InventDimFormDesignUpdate _mode)
{
    this.updateDesignForItem(_mode, toItemId, ToInventDim_ds, toInventDimCtrl);
}

private void updateDesignForItem(InventDimFormDesignUpdate _mode, ItemId _itemId, FormDataSource _inventDimDS, InventDimCtrl_Frm _inventDimCtr)
{
    InventDimParm   inventDimParmEnabled;
    Set             validDataSources;

    if (_mode == InventDimFormDesignUpdate::Init ||
        _mode == InventDimFormDesignUpdate::Active ||
        _mode == InventDimFormDesignUpdate::FieldChange)
    {
        if (!_inventDimCtr)
        {
            validDataSources = new Set(Types::String);
            validDataSources.add(_inventDimDS.name());

            _inventDimCtr = InventDimCtrl::newFromFormValidDataSources(element, validDataSources);
        }

        if (_itemId)
        {
            InventTable inventTable = InventTable::find(_itemId);

            InventDimGroupSetup inventDimGroupSetup = InventDimGroupSetup::newInventTable(inventTable);

            inventDimParmEnabled.initDimBOM(inventDimGroupSetup);
            inventDimGroupSetup.inventDimParmActive(inventDimParmEnabled);

            if (isProdBOMChangeItemUseDefaultOrderSettingsFeatureEnabled)
            {
                _inventDimDS.cursor().initFromInventTable(inventTable, InventItemOrderSetupType::Invent);
            }
        }
        else
        {
            inventDimParmEnabled.initDimBOM(InventDimGroupSetup::newDimensionGroups(0, 0, 0));

            if (isProdBOMChangeItemUseDefaultOrderSettingsFeatureEnabled)
            {
                _inventDimDS.cursor().initFromInventDim(InventDim::find(InventDim::inventDimIdBlank())); // Clear all dimensions
            }
        }

        _inventDimCtr.parmDimParmEnabled(inventDimParmEnabled);
        _inventDimCtr.parmDimParmVisible(inventDimParmEnabled);
        _inventDimCtr.formSetControls(true);
    }
    else
    {
        throw error(strfmt("@SYS54195", funcName()));
    }
}

RunBase runBase()
{
    return controller;
}

[SysClientCacheDataMethodAttribute(true)]
display ItemName fromItemName()
{
    ItemName itemName = "";

    InventTable inventTable = InventTable::find(fromItemId);

    if (inventTable)
    {
        itemName = inventTable.itemName(fromInventDim);
    }        

    return itemName;
}

[SysClientCacheDataMethodAttribute(true)]
display ItemName toItemName()
{
    ItemName itemName = "";

    InventTable inventTable = InventTable::find(toItemId);

    if (inventTable)
    {
        itemName = inventTable.itemName(toInventDim);
    }        

    return itemName;
}

public edit ItemId editFromItemId(boolean _set, ItemId _fromItemId)
{
    if (_set)
    {
        fromItemId = _fromItemId;

        controller.setFromItemId(fromItemId);

        element.updateDesignFromItem(InventDimFormDesignUpdate::Active);   

        this.tryLoadData();
    }

    return fromItemId;
}

public edit ItemId editToItemId(boolean _set, ItemId _toItemId)
{
    if (_set)
    {
        toItemId = _toItemId;

        controller.setToItemId(toItemId);

        element.updateDesignToItem(InventDimFormDesignUpdate::Active);
    }

    return toItemId;
}

public edit QtyStandard editToQty(boolean _set, ProdQty _toQty)
{        
    if (_set)
    {   
        toQty = _toQty;

        controller.setToQty(toQty);
    }

    return toQty;
}

private void tryLoadData()
{
    if (controller.canLoadData())
    {
        controller.loadData();

        ProdBOMParmChangeItem_ds.executeQuery();
    }
}

public void init()
{
    super();

    parmQuery = prodBOMParmChangeItem_ds.query().dataSourceNo(1).addRange(fieldNum(ProdBOMParmChangeItem, ParmId)); 

    this.queryBuildDataSource().addSortField(fieldNum(ProdBOMParmChangeItem, AccumulatedQty), SortOrder::Descending);            
}

void executeQuery()
{
    if (controller.isDataLoaded())
    {
        super();
    }
}

/// <summary>
/// Writing inventory dimensions from the "From Item" to the parameters.
/// </summary>
public void write()
{            
    fromInventDimId = InventDim::findOrCreate(FromInventDim).InventDimId;

    controller.setFromInventDimId(fromInventDimId);

    element.tryLoadData();
}

public void executeQuery()
{   
    super();

    FromInventDim.data(InventDim::find(fromInventDimId));
    FromInventDim_ds.setCurrent();
}

/// <summary>
/// Writing inventory dimensions from the "To Item" to the parameters.
/// </summary>
public void write()
{            
    toInventDimId = InventDim::findOrCreate(ToInventDim).InventDimId;

    controller.setToInventDimId(toInventDimId);
}

public void executeQuery()
{
    super();

    ToInventDim.data(InventDim::find(toInventDimId));
    ToInventDim_ds.setCurrent();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && this.text() && ToItemIdControl.text())
    {
        ret = controller.validateFormItems(this.text(), FromItemIdControl.text());
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && this.text() && FromItemIdControl.text())
    {
        ret = controller.validateFormItems(FromItemIdControl.text(), this.text());
    }

    return ret;
}

/// <summary>
/// Marking all records for further processing.
/// </summary>
public void clicked()
{
    super();

    controller.markUnmarkAllRecords(true);

    ProdBOMParmChangeItem_ds.research();
}

/// <summary>
/// Unmarking all records for further processing.
/// </summary>
public void clicked()
{
    super();

    controller.markUnmarkAllRecords(false);

    ProdBOMParmChangeItem_ds.research();
}

/// <summary>
/// Suggestion to utilize the on-hand inventory.
/// </summary>
public void clicked()
{
    super();

    controller.markAllOnHandRecords();

    ProdBOMParmChangeItem_ds.research();
}
