Method.Source:
[Form]
public class ProdBOMOprNum extends FormRun
{
    Map     mapRemainInventFinancial;
    Map     mapAvailPhysical;

    Map     mapPdsCWRemainInventFinancial;
    Map     mapPdsCWAvailPhysical;

    InventDimCtrl_Frm_Mov_ProdBom   inventDimFormSetup;
    InventMovement                  inventMovement;

}

public void init()
{
    mapRemainInventFinancial    = new Map(typeName2Type(extendedtypestr(recId)), Types::Real);
    mapAvailPhysical            = new Map(typeName2Type(extendedtypestr(recId)), Types::Real);

    mapPdsCWRemainInventFinancial    = new Map(typeName2Type(extendedtypestr(recId)), Types::Real);
    mapPdsCWAvailPhysical            = new Map(typeName2Type(extendedtypestr(recId)), Types::Real);

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

private void hideAllFieldsOfInventDimDataSource()
{
    SysDictTable    dictTable = new SysDictTable(tableNum(InventDim));
    FieldId         fieldId = dictTable.fieldNext(0);

    while (fieldId)
    {
        SysDictField dictField = dictTable.fieldObject(fieldId);
        if (!dictField.isSystem() || dictField.isSql())
        {
            InventDim_ds.object(fieldId).visible(false);
        }
        fieldId = dictTable.fieldNext(fieldId);
    }
}

private void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            inventMovement = InventMovement::construct(prodBOM);

            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_Mov_ProdBom::newFromFormMovement(element, inventMovement);
            }
        // initialize the form and then activate it, so no break here.
        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(), inventMovement);
            InventDimParm inventDimParmEnabled = inventDimFormSetup.parmDimParmEnabled();
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void cursorNotify(int _event)
{
    ProdBOM     prodBOMCache;
    Map         mapProdBOM;

    container   conMapRemainInventFinancial;
    container   conMapAvailPhysical;

    super(_event);

    if (_event == 0 || _event == 1)
    {
        prodBOMCache = prodBOM_DS.getFirst(0,false);
        while (prodBOMCache)
        {
            if (! mapRemainInventFinancial.exists(prodBOMCache.RecId))
            {
                if (! mapProdBOM)
                    mapProdBOM = new Map(typeName2Type(extendedtypestr(recId)), Types::Record);

                mapProdBOM.insert(prodBOMCache.RecId,prodBOMCache);
            }

            prodBOMCache = prodBOM_DS.getNext();
        }

        if (mapProdBOM)
            [conMapRemainInventFinancial,conMapAvailPhysical] = ProdBOM::prodBOMOprNumDisplayCache(mapProdBOM.pack(),
                                                                                                   mapRemainInventFinancial.pack(),
                                                                                                   mapAvailPhysical.pack());
        if (conMapRemainInventFinancial)
            mapRemainInventFinancial = Map::create(conMapRemainInventFinancial);

        if (conMapAvailPhysical)
            mapAvailPhysical         = Map::create(conMapAvailPhysical);
    }
}

display InventQtyAvailPhysicalDim availPhysicalItem(ProdBOM _prodBOM)
{
    if (! mapAvailPhysical.exists(_prodBOM.RecId))
        mapAvailPhysical.insert(_prodBOM.RecId,_prodBOM.availPhysical());

    return mapAvailPhysical.lookup(_prodBOM.RecId);
}

