void init()
{
    Query       query;
    fieldId     id;

    super();

    if (element.args().caller())
    {
        id = fieldname2id(element.args().dataset(),fieldstr(ProdTable,ProdId));

        if (id)
        {
            query = new Query();
            query.addDataSource(tablenum(ProdBOM));
            query.dataSourceNo(1).addDynalink(fieldnum(ProdBOM,ProdId),element.args().record(),id);

            prodBOMComplete_DS.query(query);
        }
    }
}

