Method.Source:
    [Form]
    public class ProdBOMVendor extends FormRun
    {
        InventDimCtrl_Frm_Mov       inventDimFormSetup;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(prodBOM);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element,movement);
        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;
        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

public int active()
{
    int ret;

    ret = super();

    return ret;
}

void init()
{
    super();

    if (!element.args().dataset())
        this.query(new Query(querystr(ProdBOMVendor)));
}

void init()
{
    Query query = new Query();
    QueryBuildRange rangeIsDeleted;

    super();

    query.addDataSource(tablenum(PurchLine));
    query.dataSourceNo(1).addDynalink(fieldnum(PurchLine,InventTransId),
                            prodBOM, fieldnum(ProdBOM,InventRefTransId));
    query.dataSourceNo(1).name(purchLine_ds.name());

    rangeIsDeleted = this.query().dataSourceTable(tablenum(PurchLine)).addRange(fieldnum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);

    purchLine_ds.query(query);
}

int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}
