[Form]
public final class ProdCalcTrans extends FormRun
{
    #resAppl

    InventTransRefId                transRefId;
    ProdTransRefType                transRefType;
    CostSheetPanel_ProdCalcTrans    costSheetPanel;

}

public void enableTransactionButton()
{
    inventTrans.visible(false);
    prodRouteTrans.visible(false);
    prodIndirectTrans.visible(false);

    if (ProdCalcTrans.RecId)
    {
        switch (prodCalcTrans.CalcType)
        {
            case ItemCalcType::Item,
                 ItemCalcType::BOM,
                 ItemCalcType::Production,
                 ItemCalcType::Burden:
                    inventTrans.visible(true);
                break;

            case ItemCalcType::Service :
                    if ((prodCalcTrans.TransRefType == ProdTransRefType::BackflushCosting ||
                        prodCalcTrans.TransRefType == ProdTransRefType::ProductionFlow))
                {
                    prodRouteTrans.visible(true);
                }
                else
                {
                    inventTrans.visible(true);
                }
                break;

            // Manufacturing
            case ItemCalcType::Process,
                 ItemCalcType::Setup,
                 ItemCalcType::Qty :
                    prodRouteTrans.visible(true);
                break;

            // Indirect
            case ItemCalcType::IndirectSurcharge,
                 ItemCalcType::IndirectRate,
                 ItemCalcType::IndirectInUnitBased,
                 ItemCalcType::IndirectOutUnitBased :
                    prodIndirectTrans.visible(true);
                break;
        }
    }
}

void init()
{
    ProdTable prodTable;

    costSheetPanel = CostSheetPanel_ProdCalcTrans::newForm(element, element.controlId(formControlStr(ProdCalcTrans, CostSheetPanelTabPage)));

    super();

    if (element.args().dataset() == tableNum(ProdTable))
    {
        transRefType = ProdTransRefType::Production;
        generalFlow.visible(false);

        prodTable = element.args().record();

        InventCostProdRecalcMultiTaskCalculate::tryRecalculateDeferedProduction(prodTable);

        if (#PmfEnabled)
        {
            if (prodTable.ProdType != ProdType::Process)
            {
                pmfCoByProdCalc.visible(false);
            }
        }
    }
    else if (element.isForProductionFlow())
    {
        transRefType = element.originType();

        generalProd.visible(false);
        overviewEstimation.visible(NoYes::No);
        calculation.visible(NoYes::No);
        consumptionSumCostingGrid.visible(NoYes::No);
        costPriceSumCostingGrid.visible(NoYes::No);
        refresh.visible(!(transRefType == ProdTransRefType::BackflushCosting));

        pmfCoByProdCalc.visible(false);
    }
    else
    {
        throw error(strFmt("@SYS19096", element.name(), tableStr(ProdTable)));
    }

    costSheetPanel.parmTransRefType(transRefType);
    costSheetPanel.init();
    this.setFormFilters();
}

private void setFormFilters()
{
    var qbd = ProdCalcTrans_DS.queryBuildDataSource();
    var qFilter = ProdCalcTrans_DS.query().addQueryFilter(qbd, fieldStr(ProdCalcTrans, CollectRefLevel));
    var qFilter2 = ProdCalcTrans_DS.query().addQueryFilter(qbd, fieldStr(ProdCalcTrans, Resource));
    var qFilter3 = ProdCalcTrans_DS.query().addQueryFilter(qbd, fieldStr(ProdCalcTrans, TransRefId));
    qFilter3.status(RangeStatus::Hidden);
}

public boolean isForProductionFlow()
{
    return (element.args().dataset() == tableNum(LeanProductionFlow)
        || element.args().dataset() == tableNum(LeanProductionFlowReference)
        || element.args().dataset() == tableNum(LeanProductionFlowCosting));
}

public void jumpToForm()
{
    if (prodCalcTrans.isResourceCost())
    {
        WrkCtrTable::jumpRefWrkCtrId(prodCalcTrans.Resource);
    }
    else if (prodCalcTrans.isItemCost())
    {
        InventTable::jumpRefItemId(prodCalcTrans.Resource);
    }
}

PlanName originPlanName()
{
    PlanName originPlanName;
    LeanProductionFlow leanProductionFlow;
    LeanProductionFlowReference leanProductionFlowReference;
    LeanProductionFlowCosting   leanProductionFlowCosting;

    if (element.args() == null || element.args().record() == null)
        return '';

    switch (element.args().dataset())
    {
        case tableNum(LeanProductionFlow):
            leanProductionFlow = element.args().record();
            originPlanName = leanProductionFlow.planReference().PlanName;
            break;

        case tableNum(LeanProductionFlowReference):
            leanProductionFlowReference = element.args().record();
            originPlanName = leanProductionFlowReference.planReference().PlanName;
            break;

        case tableNum(LeanProductionFlowCosting):
            leanProductionFlowCosting = element.args().record();
            originPlanName = leanProductionFlowCosting.getTransRefId();
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return originPlanName;
}

ProdId originProdId()
{
    ProdId originProdId;
    ProdTable prodTable;

    if (element.args() == null || element.args().record() == null)
        return '';

    switch (element.args().dataset())
    {
        case tableNum(ProdTable):
            prodTable = element.args().record();
            originProdId = prodTable.ProdId;
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return originProdId;
}

private ProdTransRefType originType()
{
    ProdTransRefType originType;

    if (element.args() == null || element.args().record() == null)
        return ProdTransRefType::Production;

    switch (element.args().dataset())
    {
        case tableNum(LeanProductionFlow):
        case tableNum(LeanProductionFlowReference):
            originType = ProdTransRefType::ProductionFlow;
            break;

        case tableNum(LeanProductionFlowCosting):
            originType = ProdTransRefType::BackflushCosting;
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return originType;
}

void run()
{
    super();

    prodCalcTrans_ds.findCaller();

    if (this.isForProductionFlow())
    {
        // Warn the user to refresh in case WIP balance is not updated
        if (!LeanWIPBalanceUpdate::checkLeanWIPBalanceUpdated(element.originPlanName()))
            Box::warning("@SYS309289");
    }
}

public void linkActive()
{
    QueryBuildDataSource    qbds;
    QueryBuildRange         qbr;

    qbds = this.query().dataSourceNo(1);

    if (element.isForProductionFlow())
    {
        qbr = SysQuery::findOrCreateRange(qbds,fieldNum(ProdCalcTrans,TransRefId));
        qbr.value(queryValue(element.originPlanName()));
    }
    else
    {
        qbr = SysQuery::findOrCreateRange(qbds,fieldNum(ProdCalcTrans,TransRefId));
        qbr.value(queryValue(element.originProdId()));
    }

    super();
}

void enableButtons()
{
    ProdTable           prodTableTmp;
    boolean             isBackflushCosting = (transRefType == ProdTransRefType::BackflushCosting);

    element.enableTransactionButton();

    if (element.isForProductionFlow() ||
        transRefType != ProdTransRefType::Production)
    {
        refresh.visible(!isBackflushCosting);
        btnProdCalcVarianceTrans.visible(isBackflushCosting);
        btnTradeArgeements.visible(false);
    }
    else
    {
        if (transRefId != prodCalcTrans.TransRefId)
        {
            transRefId = prodCalcTrans.TransRefId;

            if (prodCalcTrans.TransRefType != ProdTransRefType::Production)
            {
                throw error(Error::wrongUseOfFunction(funcName()));
            }

            prodTableTmp = ProdTable::find(prodCalcTrans.TransRefId);

            if (InventTable::find(prodTableTmp.ItemId).modelGroup().inventModelType().stdCostBased() &&
                prodTableTmp.ProdStatus == ProdStatus::Completed)
            {
                btnProdCalcVarianceTrans.enabled(true);
            }
            else
            {
                btnProdCalcVarianceTrans.enabled(false);
            }
        }

        btnTradeArgeements.enabled(prodCalcTrans.CalcType == ItemCalcType::Item ||
            prodCalcTrans.CalcType == ItemCalcType::Production ||
            prodCalcTrans.CalcType == ItemCalcType::BOM);
    }
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    this.enableButtons();

    qbds = this.query().dataSourceNo(1);

    qbds.addSortField(fieldNum(ProdCalcTrans,TransRefId));
    qbds.addSortField(fieldNum(ProdCalcTrans,TransRefType));
    qbds.addSortField(fieldNum(ProdCalcTrans,LineNum));
}

public int active()
{
    int ret;

    ret = super();

    this.enableButtons();

    return ret;
}

void executeQuery()
{
    prodCalcTrans_ds.findCaller();

    super();

    if (element.isForProductionFlow())
    {
        costSheetPanel.parmTransRefPlanName(element.originPlanName());
    }
    else
    {
        costSheetPanel.parmTransRefId(element.originProdId());
    }
    costSheetPanel.parmTransRefType(transRefType);
    costSheetPanel.fill();
}

void findCaller()
{
    ProdCalcTrans               prodCalcTransTmp;
    ProdTable                   prodTableTmp;
    LeanProductionFlow          leanProductionFlowTmp;
    LeanProductionFlowReference leanProductionFlowReferenceTmp;
    InventTransRefId            transRefIdTmp;
    ProdCollectRefProdId        collectRefProdIdTmp;

    if (transRefType == ProdTransRefType::Production)
    {
        prodTableTmp        = element.args().record();
        transRefIdTmp       = prodTableTmp.ProdId;
        collectRefProdIdTmp = prodTableTmp.CollectRefProdId;
    }
    else
    {
        if (element.args().dataset() == tableNum(LeanProductionFlow))
        {
            leanProductionFlowTmp = element.args().record();
            transRefIdTmp         = leanProductionFlowTmp.planReference().PlanName;
        }
        if (element.args().dataset() == tableNum(LeanProductionFlowReference))
        {
            leanProductionFlowReferenceTmp = element.args().record();
            transRefIdTmp                  = leanProductionFlowReferenceTmp.planReference().PlanName;
        }
        collectRefProdIdTmp   = transRefIdTmp;
    }

    select firstonly prodCalcTransTmp
        where prodCalcTransTmp.CollectRefProdId      == collectRefProdIdTmp
           && prodCalcTransTmp.CalcType              == ItemCalcType::Production
           && prodCalcTransTmp.TransRefId            == transRefIdTmp
           && prodCalcTransTmp.TransRefType          == transRefType;

    if (prodCalcTransTmp.RecId)
    {
        element.args().lookupRecord(prodCalcTransTmp);
    }
}

void clicked()
{
    LeanProductionFlowReference leanProductionFlowReference;

    if (transRefType == ProdTransRefType::ProductionFlow)
    {
        // Trigger the update of the WIP
        Debug::assert(element.args().record().TableId == tableNum(LeanProductionFlowReference));
        leanProductionFlowReference = element.args().record();
        LeanWIPBalanceUpdate::updateBalances(leanProductionFlowReference);
        prodCalcTrans_DS.research();
    }

    super();
}

/// <summary>
/// Initializes and calls PmfCoByProdCalc with the prodTable record.
/// </summary>
void clicked()
{
    ProdTable       prodTable;
    xMenuFunction   xMenuFunction;
    Args            args = new Args();

    prodTable = element.args().record();
    xMenuFunction = pmfCoByProdCalc.menufunction();
    args.record(prodTable);
    xMenuFunction.run(args);
}

/// <summary>
/// Disables the Transaction button when the Costing Sheet tab page is active.
/// </summary>
/// <param name = "_FromTab">The index of the previous active tab page.</param>
/// <param name = "_ToTab">The index of the newly active tab page.</param>
public void tabChanged(int _FromTab, int _ToTab)
{
    super(_FromTab, _ToTab);

    InventTrans.enabled(!CostSheetPanelTabPage.isActivePage());
}

