Method.Source:
[Form]
public class ProdIndirectTrans extends FormRun
{
    private boolean showPurchaseOrderId = false;
    private QueryRun mainQuery;

    [FormObservable]
    PurchaseOrderId purchaseOrderId;
}

public display PurchaseOrderId purchaseOrderId()
{
    if (!mainQuery || SysQuery::countTotal(mainQuery) == 0)
    {
        return '';
    }
    return purchaseOrderId;
}

public void init()
{
    FormRun caller = element.args().caller();
    if (!caller)
    {
        throw error(Error::missingFormActiveBuffer(element.name()));
    }
    else
    {
        switch (caller.name())
        {
            // PurchIndirectTrans
            case formStr(VendInvoiceJournal):
            case formStr(VendPackingSlipJournal):
                if (element.args().record() == null
                 ||(element.args().record().TableId != tableNum(VendPackingSlipTrans)
                 && element.args().record().TableId != tableNum(VendInvoiceTrans)))
                {
                    throw error(Error::missingFormActiveBuffer(element.name()));
                }
                showPurchaseOrderId = true;
                break;
            default: // ProdIndirectTrans
                break;
        }
    }
    super();
}

/// <summary>
/// Sets the visibility of controls based on the <c>ProdIndirectTrans</c> datasource.
/// </summary>
protected void prodIndirectTransSetVisibility()
{
    surchargeSubtype.visible(prodIndirectTrans.Type == CostSheetNodeType::CalculationSurcharge);
    rateSubtype.visible(prodIndirectTrans.Type == CostSheetNodeType::CalculationRate);
    unitBasedSubtype.visible(prodIndirectTrans.Type == CostSheetNodeType::CalculationInUnitBased
                          || prodIndirectTrans.Type == CostSheetNodeType::CalculationOutUnitBased
                          || prodIndirectTrans.Type == CostSheetNodeType::CalculationPurchUnitBased);
}

public int active()
{
    int ret;
    mainQuery = this.queryRun();
    ret = super();

    element.prodIndirectTransSetVisibility();

    return ret;
}

public void linkActive()
{
    LeanProductionFlowCosting   productionFLowCosting;
    ProdCalcTrans               prodCalcTrans;

    QueryBuildRange             qbrTransRefId;
    QueryBuildRange             qbrVoucher;
    QueryBuildRange             qbrState;
    QueryBuildRange             qbrCostCode;

    VendPackingSlipTrans        vendPackingSlipTrans;
    VendInvoiceTrans            vendInvoiceTrans;

    switch (element.args().record().TableId)
    {
        case tableNum(VendPackingSlipTrans):
            vendPackingSlipTrans = element.args().record();
            purchaseOrderId = vendPackingSlipTrans.vendPackingslipJour().PurchId;
            break;
        case tableNum(VendInvoiceTrans):
            vendInvoiceTrans = element.args().record();
            purchaseOrderId = vendInvoiceTrans.PurchID;
            break;
        case tableNum(ProdCalcTrans):
            prodCalcTrans = element.args().record();

            qbrTransRefId = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdIndirectTrans)), fieldNum(ProdIndirectTrans, TransRefId));
            qbrState = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdIndirectTrans)), fieldNum(ProdIndirectTrans, Calculated));
            qbrVoucher = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdIndirectTrans)), fieldNum(ProdIndirectTrans, VoucherCalc));
            qbrCostCode = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdIndirectTrans)), fieldNum(ProdIndirectTrans, Code));
            qbrCostCode.value(prodCalcTrans.Resource);

            if (prodCalcTrans.TransRefType == ProdTransRefType::Production)
            {
                qbrTransRefId.value(prodCalcTrans.TransRefId);
                qbrState.value(SysQuery::valueUnlimited());
                qbrVoucher.value(SysQuery::valueUnlimited());
            }

            if (prodCalcTrans.TransRefType == ProdTransRefType::ProductionFlow)
            {
                qbrTransRefId.value(prodCalcTrans.TransRefId);
                qbrState.value(queryValue(NoYes::No));
                qbrVoucher.value(SysQuery::valueEmptyString());
            }

            if (prodCalcTrans.TransRefType == ProdTransRefType::BackflushCosting)
            {
                productionFLowCosting = LeanProductionFlowCosting::find(str2int64(prodCalcTrans.TransRefId));
                qbrTransRefId.value(productionFLowCosting.displayProductionFlowName());
                qbrState.value(queryValue(NoYes::Yes));
                qbrVoucher.value(productionFLowCosting.Voucher);
            }
            break;
        default:
            if (showPurchaseOrderId)
            {
                throw error(Error::wrongUseOfFunction(funcName()));
            }
    }

    super();
}

public void init()
{
    VendPackingSlipTrans    vendPackingSlipTrans;
    VendInvoiceTrans        vendInvoiceTrans;
    QueryBuildDataSource    qbdsProdIndirectTrans;
    QueryBuildDataSource    qbdsVendInvoiceJour;
    QueryBuildDataSource    qbdsVendInvoiceTrans;
    QueryBuildDataSource    qbdsVendPackingSlipVersion;
    QueryBuildRange         range;

    super();

    switch (element.args().record().TableId)
    {
        case tableNum(VendPackingSlipTrans):
            vendPackingSlipTrans = element.args().record();
            purchaseOrderId = vendPackingSlipTrans.vendPackingslipJour().PurchId;
            qbdsProdIndirectTrans = this.query().dataSourceTable(tableNum(ProdIndirectTrans));
            qbdsProdIndirectTrans.clearLinks();
            qbdsProdIndirectTrans.addDynalink(fieldNum(ProdIndirectTrans, TransRefId), element.args().record(), fieldNum(VendPackingSlipTrans, InventTransId));
            range = qbdsProdIndirectTrans.addRange(fieldNum(ProdIndirectTrans, TransRefType));
            range.value(queryValue(ProdTransRefType::InventTransId));
            range.status(RangeStatus::Hidden);
            qbdsVendPackingSlipVersion = qbdsProdIndirectTrans.addDataSource(tableNum(VendPackingSlipVersion));
            qbdsVendPackingSlipVersion.joinMode(JoinMode::ExistsJoin);
            qbdsVendPackingSlipVersion.addDynalink(fieldNum(VendPackingSlipVersion, VendPackingSlipJour),  element.args().record(), fieldNum(VendPackingSlipTrans, VendPackingSlipJour));
            qbdsVendPackingSlipVersion.addLink(fieldNum(ProdIndirectTrans, VoucherEstimation), fieldNum(VendPackingSlipVersion, CostLedgerVoucher));
            qbdsVendPackingSlipVersion.addLink(fieldNum(ProdIndirectTrans, DateEstimation), fieldNum(VendPackingSlipVersion, AccountingDate));

            prodIndirectTrans_ds.object(fieldNum(ProdIndirectTrans, DateCalc)).visible(false);
            prodIndirectTrans_ds.object(fieldNum(ProdIndirectTrans, VoucherCalc)).visible(false);
            LedgerTransVoucherCosting.visible(false);
            break;
        case tableNum(VendInvoiceTrans):
            vendInvoiceTrans = element.args().record();
            purchaseOrderId = vendInvoiceTrans.PurchID;
            qbdsProdIndirectTrans = this.query().dataSourceTable(tableNum(ProdIndirectTrans));
            qbdsProdIndirectTrans.clearLinks();
            range = qbdsProdIndirectTrans.addRange(fieldNum(ProdIndirectTrans, TransRefType));
            range.value(queryValue(ProdTransRefType::InventTransId));
            range.status(RangeStatus::Hidden);

            qbdsVendInvoiceTrans = qbdsProdIndirectTrans.addDataSource(tableNum(VendInvoiceTrans));
            qbdsVendInvoiceTrans.joinMode(JoinMode::ExistsJoin);
            qbdsVendInvoiceTrans.addDynalink(fieldNum(VendInvoiceTrans, RecId), element.args().record(), fieldNum(VendInvoiceTrans, RecId));
            qbdsVendInvoiceTrans.addLink(fieldNum(ProdIndirectTrans, TransRefId), fieldNum(VendInvoiceTrans, InventTransId));
            qbdsVendInvoiceJour = qbdsVendInvoiceTrans.addDataSource(tableNum(VendInvoiceJour));
            qbdsVendInvoiceJour.joinMode(JoinMode::ExistsJoin);
            qbdsVendInvoiceJour.relations(true);
            qbdsVendInvoiceJour.addLink(fieldNum(ProdIndirectTrans, VoucherCalc), fieldNum(VendInvoiceJour, CostLedgerVoucher), qbdsProdIndirectTrans.name());
            qbdsVendInvoiceJour.addLink(fieldNum(ProdIndirectTrans, DateCalc), fieldNum(VendInvoiceJour, InvoiceDate), qbdsProdIndirectTrans.name());

            prodIndirectTrans_ds.object(fieldNum(ProdIndirectTrans, DateEstimation)).visible(false);
            prodIndirectTrans_ds.object(fieldNum(ProdIndirectTrans, VoucherEstimation)).visible(false);
            LedgerTransVoucherEstimation.visible(false);
            break;
        default:
            if (showPurchaseOrderId)
            {
                throw error(Error::wrongUseOfFunction(funcName()));
            }
            SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdIndirectTrans)), fieldNum(ProdIndirectTrans, TransRefType)).status(RangeStatus::Hidden);
    }
}

/// <summary>
/// Opens the <c>PurchTable</c> form.
/// </summary>
public void jumpRef()
{
    PurchTable purchTable = PurchTable::find(element.purchaseOrderId());

    Args args = new Args();
    args.caller(element);
    args.record(purchTable);

    MenuFunction::runClient(menuitemdisplaystr(PurchTable), MenuItemType::Display, true, args);
}
