        journalFormTable.parmCtrlUseReleaseFromProduction_RU(prodJournalReleaseCreate_RU);
        journalFormTable.parmCtrlJourReleaseCopy_RU(prodJournalReleaseCopy_RU);
        journalFormTable.parmCtrlJourReleaseReversal_RU(prodJournalReleaseReversal_RU);
    }
    // </GEERU>
    journalFormTable.formInitPost();
