/// <summary>
/// Set if Batch disposition code and status fields based if inventBatchId is visible
/// </summary>
public void pdsShow()
{
    if (inventDimGrid_inventBatchId.visible() == true)
    {
        pdsDispositionCode.visible(true);
        pdsDispositionStatus.visible(true);
    }
    else
    {
        pdsDispositionCode.visible(false);
        pdsDispositionStatus.visible(false);
    }
}

/// <summary>
/// Returns true if item is formula and Pmf configuration key is enabled.
/// </summary>
/// <returns>True if formula item; otherwise false.</returns>
public boolean pmfIsEnabled()
{
    return #PmfEnabled
        && prodTable.ProdType == ProdType::Process;
}

public void setmandatory()
{
    project.visible(prodJournalBOM.isProjMethodConsumed());

    prodJournalBOM_ds.object(fieldNum(ProdJournalBOM,ProjId)).mandatory(prodJournalBOM.isProjMethodConsumed());
    prodJournalBOM_ds.object(fieldNum(ProdJournalBOM,ProjCategoryId)).mandatory(prodJournalBOM.isProjMethodConsumed());
    prodJournalBOM_ds.object(fieldNum(ProdJournalBOM,ProjLinePropertyId)).mandatory(prodJournalBOM.isProjMethodConsumed());
    prodJournalBOM_ds.object(fieldNum(ProdJournalBOM,ProjSalesCurrencyId)).mandatory(prodJournalBOM.isProjMethodConsumed());
    prodJournalBOM_ds.object(fieldNum(ProdJournalBOM,bomUnitId)).mandatory(prodJournalBOM.isProjMethodConsumed());
}

