void delete()
{
    try
    {
        inDelete = true;
        ttsbegin;

        super();

        journalFormTable.datasourceDeletePost();
        journalFormTrans.journalTableData(null);

        ttscommit;
        inDelete = false;
    }
    catch(Exception::Error)
    {
        inDelete = false;
        throw Exception::Error;
    }
    catch(Exception::UpdateConflict)
    {
        inDelete = false;
        throw Exception::UpdateConflict;
    }
    catch(Exception::CodeAccessSecurity)
    {
        inDelete = false;
        throw Exception::CodeAccessSecurity;
    }
    catch(Exception::UpdateConflictNotRecovered)
    {
        inDelete = false;
        throw Exception::UpdateConflictNotRecovered;
    }
}

void init()
{
    super();

    journalFormTable.datasourceInitPost(prodJournalTable);

    Args args = element.args();
    Object caller = args.caller();

    if (caller && caller.name() == formStr(ProdJournalTable))
    {
        QueryBuildDataSource dataSource = ProdJournalTable_DS.query().dataSourceTable(tableNum(ProdJournalTable));
        dataSource.clearDynalinks();
        ProdJournalTable journal = args.record();
        dataSource.addRange(fieldnum(ProdJournalTable, JournalId)).value(queryValue(journal.JournalId));
    }
}

