public void modified()
{
    FormDataObject formBOMUnitObject;

    super();

    journalFormTrans.fieldModifiedInventTransIdPost();

    element.setmandatory();

    // If Lot Id is specified for picking list journal line,
    // the possibility to change unit type must be disabled.
    // linesBOMUnit.allowEdit method will always return correct state.
    formBOMUnitObject = prodJournalBOM_ds.object(fieldNum(ProdJournalBOM, bomUnitId));
    formBOMUnitObject.allowEdit(linesBOMUnit.allowEdit());
}

public boolean validate()
{
    boolean     ret;

    PmfProdCoBy prodCoBy;
    ProdTable   pmfProdTable;

    ret = super();

    if (#PmfEnabled)
    {
        pmfProdTable = ProdTable::find(prodJournalBOM.ProdId);

        select firstonly firstfast RecId, ProdId
            from   prodCoBy
            where  prodCoBy.InventTransId == prodJournalBOM.InventTransId
                && pmfProdTable.ProdType == ProdType::Process
                && prodCoBy.ProdId == prodJournalBOM.ProdId;

        if (ret && prodCoBy.RecId)
        {
            throw error(strFmt("@PRO356", prodCoBy.ProdId));
        }
    }

    return ret;
}

public void modified()
