public boolean validate()
{
    boolean ret;

    PmfProdCoBy prodCoBy;
    ProdTable   pmfProdTable;

    ret = super();

    if (#PmfEnabled)
    {
        pmfProdTable = ProdTable::find(prodJournalBOM.ProdId);

        select firstonly firstfast RecId, ProductType
            from   prodCoBy
            where  prodCoBy.ItemId == prodJournalBOM.ItemId
                && pmfProdTable.ProdType == ProdType::Process
                && prodCoBy.ProdId == prodJournalBOM.ProdId;

        if (ret &&
            prodCoBy.RecId &&
            prodCoBy.ProductType != PmfProductType::By_Product)
        {
            throw error("@PRO251");
        }
    }

    return ret;
}

