public boolean validate()
{
    boolean ret = super();

    if (ret && #PdsShelfEnabled)
    {
        if (ret)
        {
            ret = element.mustCheckBatchProcessExpiryDate();
        }

        if (ret)
        {
            PdsBatchDispCheck_PickList pdsCheckPick = PdsBatchDispCheck_PickList::construct();
            ret = pdsCheckPick.validateCheck(prodJournalBOM,
                                             inventDim.InventBatchId);
        }
    }

    return ret;
}

