	private void initTableIndexToProdStatusAllMap()
    {
        tableIndexToProdStatusAllMap = new Map(Types::Integer, Types::Enum);

        var enumerator = ProdStatusAllOrderedList::construct().getEnumerator();

		int tableIndex = 1;
		while (enumerator.moveNext())
        {
            tableIndexToProdStatusAllMap.insert(tableIndex, enumerator.current());
            tableIndex++;
        }
    }

    /// <summary>
    /// Creates the checkbox matrix on the Status tab.
    /// </summary>
    public void createMatrix()
    {
        boolean isProdShopEnabled = isConfigurationkeyEnabled(configurationKeyNum(ProdShop));

        table.rows(tableIndexToProdStatusAllMap.elements());
        table.columns(tableIndexToProdStatusAllMap.elements());

        for (row = 1; row <= table.rows(); row++)
        {
            for (column = 1; column <= table.columns(); column++)
            {
                if (row == column || row == table.rows())
                {
                    table.cell(column, row).editControl(noEditString);
                }
                else
                {
                    if (!isProdShopEnabled && 
						(tableIndexToProdStatusAllMap.lookup(column) == ProdStatusAll::JobScheduled || 
						 tableIndexToProdStatusAllMap.lookup(row) == ProdStatusAll::JobScheduled))
                    {
                        table.cell(column, row).editControl(noEditString);
                    }
                    else
                    {
                        table.cell(column, row).editControl(checkBox);
                        table.cell(column, row).data(NoYes::No);
                    }
                }                
			}
        }
    }

