/// <summary>
/// Helper method for the different time units supported by the form.
/// </summary>
/// <param name="_set">
/// A boolean value indicating whether the value is to be get or set.
/// </param>
/// <param name="_unitOfMeasureSymbol">
/// The unit of measure symbol to be set.
/// </param>
/// <param name="_prodTimeUnitType">
/// The type of time unit of measure to be handled.
/// </param>
/// <returns>
/// A <c>UnitOfMeasureSymbol</c> value.
/// </returns>
/// <remarks>
/// During a set operation, the method will persist the change directly in the database.
/// </remarks>
public UnitOfMeasureSymbol editTimeUnitHelper(
    boolean             _set,
    UnitOfMeasureSymbol _unitOfMeasureSymbol,
    ProdTimeUnitType    _prodTimeUnitType)
{
    UnitOfMeasure           unitOfMeasure;
    ProdTimeUnitOfMeasure   prodTimeUnitOfMeasure = ProdTimeUnitOfMeasure::findByParametersUnitType(prodParameters.RecId, _prodTimeUnitType, _set);

    if (_set)
    {
        unitOfMeasure = UnitOfMeasure::findBySymbol(_unitOfMeasureSymbol);

        if (!unitOfMeasure.RecId
            || !unitOfMeasure.belongsToUnitOfMeasureClass(UnitOfMeasureClass::Time))
        {
            _unitOfMeasureSymbol = '';
        }
        else
        {
            ttsbegin;
            prodTimeUnitOfMeasure.Parameters    = prodParameters.RecId;
            prodTimeUnitOfMeasure.TimeUnitType  = _prodTimeUnitType;
            prodTimeUnitOfMeasure.UnitOfMeasure = unitOfMeasure.RecId;
            prodTimeUnitOfMeasure.write();
            ttscommit;
        }
    }
    else
    {
        // read the value from the ProdTimeUnitOfMeasure buffer
        _unitOfMeasureSymbol = prodTimeUnitOfMeasure.unit().Symbol;
    }

    return _unitOfMeasureSymbol;
}

