/// <summary>
/// Handles relations between fields, so eg. ProdParametersDim.AccountCostingScrap is not editable
/// if prodParametersDim.CostingScrapMethod == ProdScrapMethod::Allocation.
/// </summary>
public void enableFieldsActive()
{
    bomJournalDrawNegative.enabled(!prodParameters.bomJournalReducePhysical);

    prodParameters_ds.object(fieldNum(ProdParameters, LeanCostEnableFullBatchParallelization)).allowEdit(prodParameters.LeanCostSplitLedgerVoucherTransactions);

    //<GEERU>
    if (prodParametersDim.CalculatingWIP_RU)
    {
        prodParametersDim_ds.object(fieldNum(ProdParametersDim,LedgerDimension)).allowEdit(false);
        prodParametersDim_ds.object(fieldNum(ProdParametersDim, LedgerDimension)).setValue("");
    }
    else
    {
        //</GEERU>
        if (!prodParametersDim_ds.object(fieldNum(ProdParametersDim,LedgerDimension)).allowEdit(prodParametersDim.CostingScrapMethod == ProdScrapMethod::Allocation ? false : true))
        {
            prodParametersDim_ds.object(fieldNum(ProdParametersDim, LedgerDimension)).setValue("");
        }
        //<GEERU>
    }

    prodParametersDim_ds.object(fieldNum(ProdParametersDim, CostingScrapMethod)).allowEdit         (!prodParametersDim.CalculatingWIP_RU);
    prodParametersDim_ds.object(fieldNum(ProdParametersDim, CalculatingMethod_RU)).allowEdit       (prodParametersDim.CalculatingWIP_RU);

    prodParametersDim_ds.object(fieldNum(ProdParametersDim, UseReleaseFromProduction_RU)).allowEdit(prodParametersDim.CalculatingWIP_RU);
    prodParametersDim_ds.object(fieldNum(ProdParametersDim, CalculatingMethod_RU)).allowEdit       (prodParametersDim.CalculatingWIP_RU);
    prodParametersDim_ds.object(fieldNum(ProdParametersDim, NoPostReleaseJournal_RU)).allowEdit    (prodParametersDim.CalculatingWIP_RU &&
                                                                                                    prodParametersDim.UseReleaseFromProduction_RU);
    //</GEERU>
}

/// <summary>
/// Marks the checkboxes in the status matrix based on ProdStatusParameters records.
/// </summary>
/// <param name="_inventDimId">
/// The InventDimId to search for the ProdStatusParameters records on.
/// </param>
public void fillMatrix(InventDimId _inventDimId)
{
    for (row = 1; row <= table.rows(); row++)
    {
        for (column = 1; column <= table.columns(); column++)
        {
            if (!(row == column || row == table.rows()))
            {
                if (ProdStatusParameters::find(_inventDimId,tableIndexToProdStatusAllMap.lookup(row), tableIndexToProdStatusAllMap.lookup(column),false,true))
                {
                    table.cell(column,row).data(NoYes::Yes);
                }
                else
                {
                    table.cell(column,row).data(NoYes::No);
                }
            }
        }
    }

    statusChanged = false;
}

