    /// <summary>
    /// Initializes form and datasources.
    /// </summary>
    public void init()
    {
        SysDictMenu sysDictMenu = SysDictMenu::newMenuItem(menuitemDisplayStr(ProdParameters), MenuItemType::Display);

		this.initTableIndexToProdStatusAllMap();

        this.numberSeqPreInit();

        companySpecificParameters = !(this.args().parm());

        if (!companySpecificParameters)
        {
            //Change caption to Production parameters by site
            element.form().design().caption("@SYS336278");
        }

        super();

        selectAll.enabled(! (sysDictMenu.rights() == AccessType::View));

        ProdParameters::find();
        ProdParametersDim::findDefault();
        RouteParameters::find();

        this.updateDesign(InventDimFormDesignUpdate::Init);

        this.createMatrix();

        this.numberSeqPostInit();

        InventDimSetupGrid::loadSetup();
        element.fillView();

        this.initSiteFromArgs();

        End_AccountCostingScrap.parmPostingType(LedgerPostingType::None);

        var args = this.args();
        if (args
            && args.parmEnumType() == enumNum(CostFormContext)
            && args.parmEnum() == CostFormContext::Costing)
        {
            this.enableCostingView();
        }
    }

