/// <summary>
/// Used for handling the selection in the list on the Inventory dimensions tab.
/// </summary>
public void listSelection()
{
    FormListItem    formListItem;
    int             idx;

    if (inventDimSetupGrid.RecId)
    {
        inventDimSetupGrid.write();
    }

    idx = dimensionList.getNextItem(FormListNext::Selected);

    if (idx <0)
    {
        element.setupAllowEdit(false);
        tmpFieldId = 0;
        inventDimSetupGrid_ds.executeQuery();
        inventDimSetupGrid_ds.refresh();
        return;
    }

    element.setupAllowEdit(true);

    formListItem = dimensionList.getItem(idx);

    tmpFieldId = dimensionList.getItem(idx).data();

    inventDimSetupGrid_ds.executeQuery();

    inventDimSetupGrid_ds.refresh();
}

