/// <summary>
/// Initializes number sequences.
/// </summary>
public void numberSeqPreInit()
{
    runExecuteDirect           = false;
    numberSequenceModules = [NumberSeqModule::Prod, NumberSeqModule::ProductionAndShopFloor, NumberSeqModule::Route];

    numberSeqApplicationModule = new NumberSeqModuleProduction();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

/// <summary>
/// Disables the ProdAutoPurchCollectPerByer, ProdAutoPurchCollectPerPurchAgreement if the ProdAutoPurchCollectPerVendor is set to NoYes::No.
/// Disables the ProdAutoPurchSearchPurchAgreement if the ProdAutoPurchCollectPerPurchAgreement is set to NoYes::Yes
/// </summary>
public void run()
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>
    super();

    if (prodParameters.ProdAutoPurchCollectPerVendor == NoYes::No)
    {
        prodParameters.ProdAutoPurchCollectPerBuyer = NoYes::No;
        prodAutoPurchCollectPerBuyer.value(NoYes::No);
        prodAutoPurchCollectPerBuyer.enabled(false);

        prodParameters.ProdAutoPurchCollectPerPurchAgreement = NoYes::No;
        prodAutoPurchCollectPerPurchAgreement.value(NoYes::No);
        prodAutoPurchCollectPerPurchAgreement.enabled(false);

        prodAutoPurchSearchPurchAgreement.enabled(true);
    }
    else
    {
        prodAutoPurchCollectPerBuyer.enabled(true);
        prodAutoPurchCollectPerPurchAgreement.enabled(true);

        if (prodParameters.ProdAutoPurchCollectPerPurchAgreement == NoYes::Yes)
        {
            prodParameters.ProdAutoPurchSearchPurchAgreement = NoYes::Yes;
            prodAutoPurchSearchPurchAgreement.value(NoYes::Yes);
            prodAutoPurchSearchPurchAgreement.enabled(false);
        }
    }
}

/// <summary>
/// Selects all checkboxes in the grid on the Status tab.
/// </summary>
public void selectAll()
{
    for (row = 1; row <= table.rows(); row++)
    {
        if (tableIndexToProdStatusAllMap.lookup(row) != ProdStatusAll::Completed) // an ended production can not be reset.
        {
            for (column = 1; column <= table.columns(); column++)
            {
                if (column != row || row < table.rows())
                {
                    table.cell(column,row).data(NoYes::Yes);
                }
            }
        }
    }

    table.update();
    statusChanged = true;
}

/// <summary>
/// Sets the caption to either "Production parameters by site - Site: site1" or just "Production parameters" depending on
/// what mode the form is in.
/// </summary>
public void setCaption()
{
    str     caption;

    if (!companySpecificParameters)
    {
        //Change caption to
        //Production parameters by site - Site: site1
        caption = "@SYS336278";
        if (inventDim.InventSiteId)
        {
            caption += strFmt("@SYS109019",fieldPName(InventDim,InventSiteId),inventDim.InventSiteId);
        }
    }
    else
    {
        //Production parameters
        caption = "@SYS336277";
    }

    this.design().caption(caption);
}

