/// <summary>
/// This method is used in connection with showing the inventory dimensions.
/// </summary>
/// <param name="_mode">
/// Should either be InventDimFormDesignUpdate::Init or InventDimFormDesignUpdate::Active.
/// </param>
/// <exception cref="Exception::Error">
/// The parameter expected should either be InventDimFormDesignUpdate::Init or InventDimFormDesignUpdate::Active
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:

            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active:

            //Make sure that only the site dimension are shown
            inventDimParm.InventSiteIdFlag = NoYes::Yes;

            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.parmDimParmEnabled(inventDimParm);
            inventDim_ds.allowEdit(prodParametersDim.RecId == 0);

            inventDimFormSetup.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Creates/deletes ProdStatusParameters records depending on what checkboxes has been marked in the Status tab.
/// </summary>
/// <param name="_inventDimId">
/// The InventDimId to save status parameters under.
/// </param>
public void updateProdStatusParameters(InventDimId _inventDimId)
{
    boolean                 recordSelected;
    boolean                 recordExist;

    ProdStatusParameters    prodStatusInsert;
    ProdStatusParameters    prodStatusDelete;

    ttsbegin;

    for (row=1; row<=table.rows(); row++)
    {
        for (column=1; column<=table.columns(); column++)
        {
            if (column != row)
            {
                ProdStatusParameters::updateParameter(_inventDimId, tableIndexToProdStatusAllMap.lookup(row), tableIndexToProdStatusAllMap.lookup(column), table.cell(column, row).data());
            }
        }
    }

    statusChanged = false;

    ttscommit;
}

/// <summary>
/// Validates the existence of conversion methods between the time unites.
/// </summary>
/// <returns>
/// true if the time units are valid; otherwise false.
/// </returns>
public boolean validateTimeUnits()
{
    UnitOfMeasure unitOfMeasureSeconds  = ProdTimeUnitOfMeasure::findByParametersUnitType(prodParameters.RecId, ProdTimeUnitType::Seconds).unit();
    UnitOfMeasure unitOfMeasureMinutes  = ProdTimeUnitOfMeasure::findByParametersUnitType(prodParameters.RecId, ProdTimeUnitType::Minutes).unit();
    UnitOfMeasure unitOfMeasureHours    = ProdTimeUnitOfMeasure::findByParametersUnitType(prodParameters.RecId, ProdTimeUnitType::Hours).unit();
    UnitOfMeasure unitOfMeasureDays     = ProdTimeUnitOfMeasure::findByParametersUnitType(prodParameters.RecId, ProdTimeUnitType::Days).unit();

    return ProdTimeUnitOfMeasure::validateTimeUnits(unitOfMeasureSeconds, unitOfMeasureMinutes, unitOfMeasureHours, unitOfMeasureDays);
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // this is not supposed to prevent validation of the record. it's just a hook
    // to trigger the validation of the time units
    element.validateTimeUnits();

    return ret;
}

