public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    ret = super(_forceUpdate);

    //If values in the status table has been modified, then make sure these changes are saved
    if (statusChanged)
    {
        element.updateProdStatusParameters(prodParametersDim.InventDimId);
    }

    return ret;
}

public void create(boolean _append = false)
{
    int         i;

    inventDim_ds.allowEdit(true);

    super(_append);

    //Set focus to the first visible control in the grid on the Overview tab
    for (i = 1; i<= groupInventDim.controlCount(); i++)
    {
        if (groupInventDim.controlNum(i).visible())
        {
            groupInventDim.controlNum(i).setFocus();
            break;
        }
    }
}

