}

public str getLabel(int _input)
{
    if (!tableIndexToProdStatusAllMap.exists(_input))
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    ProdStatusAll status = tableIndexToProdStatusAllMap.lookup(_input);

    return enum2str(status);
}

public str rowLabel(int _row)
{
    return this.getLabel(_row);
}

