void close()
{
    Common               buffer;
    ProdId               prodNum;

    if (element.args().caller() && element.args().record().TableId)
    {
        buffer = element.args().record();

        if (fieldName2id(buffer.TableId,fieldStr(ProdTable,ProdId)))
        {
            prodNum = buffer.(fieldName2id(buffer.TableId,fieldStr(ProdTable,ProdId)));
            try
            {
                ProdTable::find(prodNum).updateRoute();
            }
            catch (Exception::Error)
            {
                exceptionTextFallThrough();
            }
        }
    }

    super();
}

