/// <summary>
/// Filters the query to only show the operations which have been scheduled on
/// the specific resource or on the resources which are part of the resource group.
/// </summary>
private void addResourceFilter()
{
    QueryBuildDataSource    qbdsProdRoute;
    QueryBuildDataSource    qbdsWrkCtrCapResFilter;
    QueryBuildDataSource    qbdsWrkCtrResourceAndGroupView;

    qbdsProdRoute = this.query().dataSourceTable(tableNum(ProdRoute));
    qbdsProdRoute.clearDynalinks();
    qbdsProdRoute.clearLinks();
    this.query().validTimeStateDateRange(dateNull(),maxDate());

    qbdsWrkCtrCapResFilter = qbdsProdRoute.addDataSource(tableNum(WrkCtrCapRes), 'WrkCtrCapResFilter');
    qbdsWrkCtrCapResFilter.joinMode(JoinMode::ExistsJoin);
    qbdsWrkCtrCapResFilter.relations(true);

    qbdsWrkCtrResourceAndGroupView = qbdsWrkCtrCapResFilter.addDataSource(tableNum(WrkCtrResourceAndGroupView), 'WrkCtrResourceAndGroupViewFilter');
    qbdsWrkCtrResourceAndGroupView.joinMode(JoinMode::ExistsJoin);
    qbdsWrkCtrResourceAndGroupView.addLink(fieldNum(WrkCtrCapRes,WrkCtrId), fieldNum(WrkCtrResourceAndGroupView, WrkCtrId));

    qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView,ValidFrom)).value(
            strFmt('(%1.%2 <= %3.%4)',
                    qbdsWrkCtrResourceAndGroupView.name(),
                    fieldStr(WrkCtrResourceAndGroupView,ValidFrom),
                    qbdsWrkCtrCapResFilter.name(),
                    fieldStr(WrkCtrCapRes,TransDate))
    );

    qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView,ValidTo)).value(
            strFmt('(%1.%2 >= %3.%4)',
                    qbdsWrkCtrResourceAndGroupView.name(),
                    fieldStr(WrkCtrResourceAndGroupView,ValidTo),
                    qbdsWrkCtrCapResFilter.name(),
                    fieldStr(WrkCtrCapRes,TransDate))
    );

    // Add range on resource and group. The values of these will be set in LinkActive
    rangeWrkCtrId       = qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView,WrkCtrId));
    rangeWrkCtrIdGroup  = qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView,WrkCtrIdGroup));
}

