public int active()
{
    int ret;
    boolean isProjMethodConsumed;

    ret = super();

    element.setTimes();

    isProjMethodConsumed = prodRoute.prodTable().isProjMethodConsumed();
    if (isProjMethodConsumed)
    {
        skipWrite = true;
        prodRouteProjSetup_ds.executeQuery();
        prodRouteProjProcess_ds.executeQuery();
        prodRouteProjQty_ds.executeQuery();
        skipWrite = false;
    }

    costControlCommittedCost.enabled(isProjMethodConsumed);

    wrkCtrActivityRequirementSet_ds.allowEdit(prodRoute_ds.allowEdit());

    element.enableFieldsActive();

    return ret;
}

public void deleteMarked()
{
    ProdRoute       prodRouteFind;

    inDeleteMarked = true;

    super();

    inDeleteMarked = false;

    select firstonly prodRouteFind
        index NumOprNumIdx
        where prodRouteFind.ProdId     == prodRoute.ProdId &&
              prodRouteFind.OprNum     >= prodRoute.OprNum;

    skipWrite = true;
    prodRoute_DS.executeQuery();

    if (prodRouteFind)
        prodRoute_DS.positionToRecord(prodRouteFind);
    skipWrite = false;
}

void write()
{
    boolean isCreate = (prodRoute.RecId == 0);
    boolean isOprStatusModified = false;

    if (skipWrite)
        return;

    ttsbegin;

    if (prodRoute.isOprStatusModified())
    {
        ProdRoute::updateOprStatusFromForm(prodRoute);

        isOprStatusModified = true;
    }

    if (prodRoute.isProjMethodConsumed())
    {
        prodRouteProjSetup.ProdId = prodRoute.ProdId;
        prodRouteProjSetup.OprNum = prodRoute.OprNum;
        prodRouteProjSetup.OprPriority = prodRoute.OprPriority;
        prodRouteProjSetup.CalcType = ItemCalcType::Setup;
        if (prodRouteProjSetup.RecId)
            prodRouteProjSetup.update();
        else
            prodRouteProjSetup.insert();

        prodRouteProjProcess.ProdId = prodRoute.ProdId;
        prodRouteProjProcess.OprNum = prodRoute.OprNum;
        prodRouteProjProcess.OprPriority = prodRoute.OprPriority;
        prodRouteProjProcess.CalcType = ItemCalcType::Process;
        if (prodRouteProjProcess.RecId)
            prodRouteProjProcess.update();
        else
            prodRouteProjProcess.insert();

        prodRouteProjQty.ProdId = prodRoute.ProdId;
        prodRouteProjQty.OprNum = prodRoute.OprNum;
        prodRouteProjQty.OprPriority = prodRoute.OprPriority;
        prodRouteProjQty.CalcType = ItemCalcType::Qty;
        if (prodRouteProjQty.RecId)
            prodRouteProjQty.update();
        else
            prodRouteProjQty.insert();
    }

    super();

    prodRoute.updateCheckFlag();

    if (activityToCopy != 0)
    {
        //If the route was initialized from a base operation then also copy the activity requirements
        WrkCtrActivityRequirementSet::copyRequirements(activityToCopy, prodRoute.activity());
        activityToCopy = 0;
    }

    if (isCreate)
    {
        RouteOpr routeOpr = RouteOpr::findRecId(prodRoute.RouteOprRefRecId);
        if (routeOpr)
        {
            Docu::copy(routeOpr, prodRoute);
        }
    }

    ttscommit;

    if (isOprStatusModified && ProdRoute::existSecondary(prodRoute.ProdId, prodRoute.OprNum))
    {
        prodRoute_ds.research(true);
    }
}

void linkActive()
{
    ProdTable   prodTable;
    WrkCtrTable wrkCtrTable;

    switch (element.args().dataset())
    {
        case tableNum(ProdTable):
            prodTable       = element.args().record();
            prodStatusType  = prodTable.statusClient();

            element.allowEdit(prodStatusType.routeAllowEdit());
            routeUpdateButton.enabled(prodStatusType.runRouteUpdate(true));
            break;

        case tableNum(WrkCtrTable):
            wrkCtrTable = element.args().record();
            if (wrkCtrTable.IsIndividualResource)
            {
                rangeWrkCtrId.value(queryValue(wrkCtrTable.WrkCtrId));
                rangeWrkCtrIdGroup.value(SysQuery::valueUnlimited());
            }
            else
            {
                rangeWrkCtrId.value(SysQuery::valueUnlimited());
                rangeWrkCtrIdGroup.value(queryValue(wrkCtrTable.WrkCtrId));
            }
            break;
    }

    super();
}

void init()
{
    ProdTable prodTable;

    activityToCopy = 0;

    super();

    prodRoute_ds.cacheAddMethod(tableMethodStr(ProdRoute,qtyGood));
    prodRoute_ds.cacheAddMethod(tableMethodStr(ProdRoute,qtyError));
    prodRoute_ds.cacheAddMethod(tableMethodStr(ProdRoute,qtyHours));
    prodRoute_ds.cacheAddMethod(tableMethodStr(ProdRoute,qualityOrderStatusDisplay));

    prodRoute_ds.cacheAddMethod(tableMethodStr(ProdRoute,pdsCWQtyGood));
    prodRoute_ds.cacheAddMethod(tableMethodStr(ProdRoute,pdsCWQtyError));

    if (element.args().dataset() == tableNum(ProdTable))
    {
        this.query().dataSourceTable(tableNum(ProdRoute)).addSortIndex(indexNum(ProdRoute,NumOprNumIdx));

        prodTable = element.args().record();
        this.query().dataSourceTable(tableNum(ProdRoute)).addDynalink(fieldnum(ProdRoute, ProdId),
                                     prodTable,
                                     fieldname2id(tablenum(ProdTable), fieldstr(ProdTable, ProdId)));
    }
    else
    {
        if (element.args().dataset() == tableNum(WrkCtrTable))
        {
            this.addResourceFilter();
        }
    }
}

void create(boolean append = false)
{
    ProdRoute   oldRoute;

    oldRoute = prodRoute.data();

    element.prodRouteForceWrite();

    super(append);

    element.setPrevOprNum(prodRoute,oldRoute,append);

    if (prodRoute.isProjMethodConsumed())
    {
        prodRouteProjSetup_ds.create();
        prodRouteProjProcess_ds.create();
        prodRouteProjQty_ds.create();

        element.enableFieldsActive();
    }
}

void delete()
{
    boolean     doResearch;
    ProdId      prodId_Save;
    OprNum      oprNum_Save;
    ProdRoute   tmpProdRoute;

    ttsbegin;

    doResearch = prodRoute.OprPriority == RouteOprPriority::Primary ? true : false;
    prodId_Save = prodRoute.ProdId;
    oprNum_Save = prodRoute.OprNum;

    super();

    prodRoute.updateCheckFlag();

    ttscommit;

    if (doResearch && ! inDeleteMarked)
    {
        select reverse tmpProdRoute
            index NumOprNumIdx
            where tmpProdRoute.ProdId     == prodId_Save &&
                  tmpProdRoute.OprNum     <  oprNum_Save;

        if (!tmpProdRoute)
            select tmpProdRoute
                index NumOprNumIdx
                where tmpProdRoute.ProdId     == prodId_Save &&
                      tmpProdRoute.OprNum     >  oprNum_Save;

        if (tmpProdRoute)
        {
            skipWrite = true;
            prodRoute_DS.executeQuery();
            prodRoute_DS.positionToRecord(tmpProdRoute);
            skipWrite = false;
        }
    }
    element.enableFieldsActive();
}

